def convert_to_avestan(text: str) -> str:
    """Convert Persian or English text to Avestan script"""
    result = []
    for char in text:
        if char in persian_to_avestan_mapping:
            result.append(persian_to_avestan_mapping[char])
        elif char in english_to_avestan_mapping:
            result.append(english_to_avestan_mapping[char])
        else:
            result.append(char)
    return ''.join(result)


def convert_to_oracle_bone(text):
    """ØªØ¨Ø¯ÛŒÙ„ Ù…ØªÙ† Ø¨Ù‡ Ø®Ø· Ú†ÛŒÙ†ÛŒ Ø¨Ø§Ø³ØªØ§Ù†"""
    result = []
    for char in text:
        # Ù†Ø±Ù…Ø§Ù„ Ø³Ø§Ø²ÛŒ Ú©Ø§Ø±Ø§Ú©ØªØ±Ù‡Ø§
        char = unicodedata.normalize('NFKC', char)
        # ØªØ±Ø¬Ù…Ù‡ Ø¨Ø§ Ø­Ø³Ø§Ø³ÛŒØª Ú©Ù…ØªØ± Ø¨Ù‡ Ø¨Ø²Ø±Ú¯ÛŒ/Ú©ÙˆÚ†Ú©ÛŒ
        translated = oracle_bone_dict.get(char, oracle_bone_dict.get(char.lower(), char))
        result.append(translated)

    return ''.join(result)


