from ophyd import Signal
from ophyd.sim import NullStatus

from aps.beamline_driver.motion_management.facade import IMotionManager, Movement, MethodCallingType

class Motor(Signal):
    def __init__(self,
                 name: str = "M1",
                 value: float = 0.0,
                 motion_manager: IMotionManager = None,
                 verbose: bool = False):
        super(Motor, self).__init__(name=name, value=value)
        self.__motion_manager = motion_manager
        self.__verbose        = verbose

    def set(self, value, *, timeout=None, settle_time=None, **kwargs):
        self.__motion_manager.move_motor(self.name, value, movement=Movement.ABSOLUTE)
        self._readback = self.__motion_manager.get_motor_position(self.name)

        if self.__verbose: print("called motion manager", self.name, value, self._readback)

        return NullStatus()

    def put(self, *args, **kwargs):
        self.set(*args, **kwargs).wait()
