http_endpoint.request_body_spec:
{
  "description": "The list of tasks to add as dependents.",
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "data": {
            "description": "A set of dependent tasks.",
            "type": "object",
            "properties": {
              "dependents": {
                "description": "An array of task gids that are dependents of the given task.",
                "type": "array",
                "items": {
                  "type": "string"
                }
              }
            },
            "example": {
              "dependents": [
                "133713",
                "184253"
              ]
            }
          }
        }
      }
    }
  }
}
