http_endpoint.request_body_spec:
{
  "description": "The list of tasks to set as dependencies.",
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "data": {
            "type": "object",
            "properties": {
              "dependencies": {
                "description": "An array of task gids that a task depends on.",
                "type": "array",
                "items": {
                  "type": "string"
                }
              }
            },
            "example": {
              "dependencies": [
                "133713",
                "184253"
              ]
            }
          }
        }
      }
    }
  }
}
