http_endpoint.request_body_spec:
{
  "description": "A JSON payload specifying the parent object to export.",
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "data": {
            "type": "object",
            "description": "A *graph_export* request starts a job to export data starting from a parent object.",
            "properties": {
              "parent": {
                "description": "Globally unique ID of the parent object: goal, project, portfolio, or team.",
                "type": "string",
                "example": "181636"
              }
            }
          }
        }
      }
    }
  }
}
