{
  "description": "Information about the item being removed.",
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "data": {
            "type": "object",
            "required": [
              "item"
            ],
            "properties": {
              "item": {
                "description": "The item to remove from the portfolio.",
                "type": "string",
                "example": "1331"
              }
            }
          }
        }
      }
    }
  }
}
