http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "candidateId": {
            "type": "object",
            "description": "Schema reference broken: #/paths/~1interviewerPool.addUser/post/requestBody/content/application~1json/schema/properties/userId",
            "additionalProperties": true,
            "x-moar-broken-reference": "#/paths/~1interviewerPool.addUser/post/requestBody/content/application~1json/schema/properties/userId"
          },
          "note": {
            "oneOf": [
              {
                "type": "string",
                "description": "The note to add to the candidate",
                "example": "Strong candidate, very interested in the company"
              },
              {
                "type": "object",
                "description": "Note content",
                "properties": {
                  "type": {
                    "type": "string",
                    "enum": [
                      "text/plain",
                      "text/html"
                    ],
                    "description": "The content type of the note. For notes submitted with a type of text/html we support the elements listed below. Any unsupported elements will be stripped out of the note's content before posting.\n- Bold `<b>`\n- Italic `<i>`\n- Underline `<u>`\n- Links `<a>`\n- Bulleted Lists - `<ul>`, `<li>`\n- Ordered Lists - `<ol>`, `<li>`\n- Code - `<code>`\n- Code Block - `<pre>`\n"
                  },
                  "value": {
                    "type": "string"
                  }
                },
                "required": [
                  "type",
                  "value"
                ]
              }
            ]
          },
          "sendNotifications": {
            "type": "boolean",
            "description": "Whether or not users who are subscribed to the candidate should be notified that the note was posted. Default is false.",
            "default": false
          },
          "isPrivate": {
            "type": "boolean",
            "description": "Whether the note should be marked as private. Private notes require \"Allow access to non-offer private fields?\" API key permission to create and view. Default is false.",
            "default": false
          },
          "createdAt": {
            "type": "string",
            "example": "2022-08-12T20:29:56.964Z",
            "format": "date-time"
          }
        },
        "required": [
          "candidateId",
          "note"
        ]
      }
    }
  }
}
