http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "required": [
          "name"
        ],
        "properties": {
          "name": {
            "type": "string",
            "description": "The name of the department or team",
            "example": "Engineering Operations"
          },
          "externalName": {
            "type": "string",
            "description": "An alternate candidate-facing name for this department that will be used on Ashby job boards\n",
            "example": "Engineering"
          },
          "parentId": {
            "type": "string",
            "format": "uuid",
            "description": "The id of the department's parent department",
            "example": "1be42b8e-cafd-4beb-8121-f4981eb20f42"
          }
        }
      }
    }
  }
}
