http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "properties": {
          "webhookType": {
            "type": "string",
            "enum": [
              "applicationSubmit",
              "applicationUpdate",
              "candidateHire",
              "candidateStageChange",
              "candidateDelete",
              "candidateMerge",
              "interviewPlanTransition",
              "interviewScheduleCreate",
              "interviewScheduleUpdate",
              "jobPostingUpdate",
              "jobPostingPublish",
              "jobPostingUnpublish",
              "offerCreate",
              "offerUpdate",
              "offerDelete",
              "pushToHRIS",
              "surveySubmit"
            ]
          },
          "requestUrl": {
            "type": "string",
            "description": "The URL the webhook will send requests to."
          },
          "secretToken": {
            "type": "string",
            "description": "The secret token used to sign the webhook request. See our documentation [here](https://developers.ashbyhq.com/docs/authenticating-webhooks) for more information.\n"
          }
        },
        "required": [
          "webhookType",
          "requestUrl",
          "secretToken"
        ]
      }
    }
  }
}
