http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "cursor": {
            "type": "string",
            "description": "Opaque cursor indicating which page of results to fetch"
          },
          "syncToken": {
            "type": "string",
            "description": "Opaque token representing the last time a full set of results was fetched."
          }
        }
      }
    }
  }
}
