http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "title": {
            "type": "string",
            "example": "Software Engineer",
            "description": "The title of the job to search for"
          },
          "requisitionId": {
            "type": "string",
            "example": "REQ-2471",
            "description": "The requisition ID of the job to search for"
          }
        },
        "anyOf": [
          {
            "required": [
              "title"
            ]
          },
          {
            "required": [
              "requisitionId"
            ]
          }
        ]
      }
    }
  }
}
