http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "jobId": {
            "allOf": [
              {
                "description": "The unique id of the job to update."
              },
              {
                "type": "object",
                "description": "Schema reference broken: #/paths/~1interviewerPool.addUser/post/requestBody/content/application~1json/schema/properties/userId",
                "additionalProperties": true,
                "x-moar-broken-reference": "#/paths/~1interviewerPool.addUser/post/requestBody/content/application~1json/schema/properties/userId"
              }
            ]
          },
          "compensationTiers": {
            "type": "array",
            "items": {
              "type": "object",
              "description": "A valid compensation range that can be offered to candidates",
              "properties": {
                "title": {
                  "type": "string",
                  "example": "Zone A",
                  "description": "A label that describes the entire range to applicants. Title is required for multi-tier compensation ranges."
                },
                "additionalInformation": {
                  "type": "string",
                  "example": "Signing bonus available",
                  "description": "Supplementary information about the compensation"
                },
                "components": {
                  "type": "array",
                  "description": "The individual components that make up this compensation range",
                  "items": {
                    "allOf": [
                      {
                        "type": "object",
                        "properties": {
                          "compensationType": {
                            "type": "string",
                            "enum": [
                              "Salary",
                              "EquityPercentage",
                              "EquityCashValue",
                              "Commission",
                              "Bonus"
                            ],
                            "example": "Salary",
                            "description": "The type of compensation this component represents\n"
                          },
                          "interval": {
                            "type": "string",
                            "enum": [
                              "NONE",
                              "1 TIME",
                              "1 HOUR",
                              "1 DAY",
                              "1 WEEK",
                              "2 WEEK",
                              "1 MONTH",
                              "2 MONTH",
                              "1 YEAR",
                              "6 MONTH",
                              "0.5 MONTH",
                              "3 MONTH"
                            ],
                            "example": "1 YEAR",
                            "description": "The frequency at which this compensation is given"
                          },
                          "currencyCode": {
                            "type": "string",
                            "example": "EUR",
                            "description": "For non `EquityPercentage` components, the [ISO 4217](https://en.wikipedia.org/wiki/ISO_4217)\ncurrency code of the compensation range\n"
                          },
                          "label": {
                            "type": "string",
                            "example": "Estimated Salary",
                            "description": "An optional label that describes this compensation range to applicants"
                          },
                          "minValue": {
                            "oneOf": [
                              {
                                "type": "number"
                              },
                              {
                                "type": "null"
                              }
                            ],
                            "example": 72000.1,
                            "description": "The lower end of the compensation range"
                          },
                          "maxValue": {
                            "oneOf": [
                              {
                                "type": "number"
                              },
                              {
                                "type": "null"
                              }
                            ],
                            "example": 100000,
                            "description": "The higher end of the compensation range"
                          }
                        },
                        "required": [
                          "compensationType",
                          "interval"
                        ]
                      },
                      {
                        "type": "object",
                        "required": [
                          "minValue",
                          "maxValue"
                        ]
                      }
                    ]
                  },
                  "example": [
                    {
                      "compensationType": "Salary",
                      "interval": "1 YEAR",
                      "currencyCode": "EUR",
                      "minValue": 72023.45,
                      "maxValue": 100000
                    },
                    {
                      "compensationType": "EquityPercentage",
                      "interval": "NONE",
                      "minValue": 1,
                      "maxValue": 2.511
                    },
                    {
                      "compensationType": "Bonus",
                      "interval": "1 YEAR",
                      "minValue": null,
                      "maxValue": null
                    }
                  ]
                }
              },
              "required": [
                "components"
              ]
            }
          }
        },
        "required": [
          "jobId",
          "compensationTiers"
        ]
      }
    }
  }
}
