http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "event_type": {
            "type": "string",
            "format": "uri",
            "example": "https://api.calendly.com/event_types/AAAAAAAAAAAAAAAA"
          },
          "name": {
            "type": "string",
            "maxLength": 55,
            "example": "15 Minute Meeting"
          },
          "duration": {
            "type": "integer",
            "minimum": 1,
            "maximum": 720,
            "example": 15,
            "description": "Must be one of the provided duration options. If duration options aren't provided then duration must be one of the duration options inherited from the event type."
          },
          "duration_options": {
            "type": "array",
            "items": {
              "type": "integer"
            },
            "example": [
              1,
              13,
              15,
              720
            ],
            "description": "A maximum of 4 unique options is allowed. Each option must be >= 1 and <= 720."
          },
          "period_type": {
            "type": "string",
            "enum": [
              "available_moving",
              "moving",
              "fixed",
              "unlimited"
            ],
            "example": "fixed"
          },
          "start_date": {
            "type": "string",
            "format": "date",
            "example": "2019-01-02",
            "description": "is required when `period_type` is 'fixed'\nFormat: `YYYY-MM-DD`"
          },
          "end_date": {
            "type": "string",
            "format": "date",
            "example": "2019-01-03",
            "description": "is required when `period_type` is 'fixed'\nFormat: `YYYY-MM-DD`"
          },
          "max_booking_time": {
            "type": "integer",
            "example": 300,
            "description": "is required when `period_type` is 'moving' or 'available_moving'"
          },
          "hide_location": {
            "type": "boolean",
            "description": "determines if a location is hidden until invitee books a spot, only respected when there is a single custom location configured"
          },
          "location_configurations": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "location": {
                  "type": "string",
                  "maxLength": 255,
                  "example": "123 Abc St.",
                  "description": "is only supported when `kind` is 'physical', 'custom' or 'ask_invitee'"
                },
                "additional_info": {
                  "type": "string",
                  "maxLength": 255,
                  "example": "Example additional information",
                  "description": "is only supported when `kind` is 'physical' or 'inbound_call' "
                },
                "phone_number": {
                  "type": "string",
                  "example": "+1 888-888-8888",
                  "description": "is required when `kind` is 'inbound_call'"
                },
                "position": {
                  "type": "integer",
                  "example": 0
                },
                "kind": {
                  "type": "string",
                  "enum": [
                    "physical",
                    "ask_invitee",
                    "custom",
                    "outbound_call",
                    "inbound_call",
                    "google_conference",
                    "gotomeeting_conference",
                    "microsoft_teams_conference",
                    "webex_conference",
                    "zoom_conference"
                  ],
                  "example": "physical"
                }
              }
            }
          },
          "availability_rule": {
            "type": "object",
            "properties": {
              "rules": {
                "type": "array",
                "description": "are required when an availability rule is provided",
                "items": {
                  "type": "object",
                  "properties": {
                    "type": {
                      "type": "string",
                      "enum": [
                        "wday",
                        "date"
                      ],
                      "example": "wday"
                    },
                    "wday": {
                      "type": "string",
                      "enum": [
                        "sunday",
                        "monday",
                        "tuesday",
                        "wednesday",
                        "thursday",
                        "friday",
                        "saturday"
                      ],
                      "example": "friday",
                      "description": "is required when `type` is 'wday'"
                    },
                    "date": {
                      "type": "string",
                      "format": "date",
                      "example": "2019-01-02",
                      "description": "is required when `type` is 'date'\nFormat: `YYYY-MM-DD`"
                    },
                    "intervals": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "from": {
                            "type": "string",
                            "pattern": "(\\d\\d):(\\d\\d)",
                            "example": "07:00",
                            "description": "Format: `\"hh:mm\"`"
                          },
                          "to": {
                            "type": "string",
                            "example": "11:00",
                            "description": "Format: `\"hh:mm\"`",
                            "pattern": "(\\d\\d):(\\d\\d)"
                          }
                        }
                      }
                    }
                  }
                }
              },
              "timezone": {
                "type": "string",
                "example": "America/New_York",
                "description": "is required when an availability rule is provided"
              }
            }
          }
        },
        "required": [
          "event_type"
        ]
      },
      "examples": {
        "example-1": {
          "value": {
            "event_type": "https://api.calendly.com/event_types/AAAAAAAAAAAAAAAA",
            "name": "15 Minute Meeting",
            "duration": 15,
            "duration_options": [
              1,
              13,
              15,
              720
            ],
            "period_type": "available_moving",
            "start_date": "2019-01-02",
            "end_date": "2019-01-03",
            "max_booking_time": 300,
            "hide_location": true,
            "location_configurations": [
              {
                "location": "123 Abc St.",
                "additional_info": "Example additional information",
                "position": 0,
                "kind": "physical"
              }
            ],
            "availability_rule": {
              "rules": [
                {
                  "type": "wday",
                  "wday": "sunday",
                  "date": "2019-01-02",
                  "intervals": [
                    {
                      "from": "07:00",
                      "to": "11:00"
                    }
                  ]
                }
              ],
              "timezone": "America/New_York"
            }
          }
        }
      }
    }
  },
  "description": ""
}
