http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "required": [
          "event_type",
          "start_time",
          "invitee"
        ],
        "properties": {
          "event_type": {
            "type": "string",
            "x-stoplight": {
              "id": "a7jkew1bxxq4j"
            },
            "format": "uri",
            "description": "Canonical reference (unique identifier) for the event type being scheduled ",
            "example": "https://api.calendly.com/event_types/AAAAAAAAAAAAAAAA"
          },
          "start_time": {
            "type": "string",
            "x-stoplight": {
              "id": "esu3e9ko2ic4f"
            },
            "format": "date-time",
            "description": "The start time in UTC of the scheduled event",
            "example": "2019-08-07T06:05:04.321123Z"
          },
          "invitee": {
            "x-stoplight": {
              "id": "33r4biixcw7aa"
            },
            "type": "object",
            "required": [
              "email",
              "timezone"
            ],
            "properties": {
              "name": {
                "type": "string",
                "x-stoplight": {
                  "id": "0fmuo5ttqa3ys"
                },
                "description": "The full name of the invitee. **Required if** `first_name` **is not provided**",
                "example": "John Smith"
              },
              "first_name": {
                "type": "string",
                "x-stoplight": {
                  "id": "mtx5mufknvhuj"
                },
                "description": "The first name of the invitee. **Required if** `name` **is not provided**",
                "example": "John"
              },
              "last_name": {
                "type": "string",
                "x-stoplight": {
                  "id": "r96jzm1mdj3fv"
                },
                "description": "The last name of the invitee",
                "example": "Smith"
              },
              "email": {
                "type": "string",
                "x-stoplight": {
                  "id": "2nkp8zp5zotz0"
                },
                "description": "The email of the invitee",
                "example": "test@example.com"
              },
              "timezone": {
                "type": "string",
                "x-stoplight": {
                  "id": "gsxlntypi4xwm"
                },
                "description": "The timezone of the invitee",
                "example": "America/New_York"
              },
              "text_reminder_number": {
                "type": "string",
                "x-stoplight": {
                  "id": "up34qo0c1sxon"
                },
                "description": "Invitee's phone number for SMS reminders. Must be a valid phone number (e.g. +14155551234)",
                "example": "+1 888-888-8888"
              }
            }
          },
          "location": {
            "$ref": "./models/InviteeLocation.yaml"
          },
          "questions_and_answers": {
            "type": "array",
            "items": {
              "$ref": "./models/InviteeQuestionAndAnswer.yaml"
            }
          },
          "tracking": {
            "$ref": "./models/InviteeTracking.yaml"
          },
          "event_guests": {
            "type": "array",
            "x-stoplight": {
              "id": "ws09zf1b2zd2i"
            },
            "description": "Emails of invitee guests. Max 10.",
            "maxItems": 10,
            "items": {
              "x-stoplight": {
                "id": "fy506ehfvcubw"
              },
              "type": "string",
              "format": "email",
              "example": "janedoe@calendly.com"
            }
          }
        }
      }
    }
  }
}
