http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "x-stoplight": {
              "id": "0tj6bpbhb3zax"
            },
            "description": "Event type name",
            "example": "My Meeting",
            "maxLength": 55
          },
          "host": {
            "type": "string",
            "x-stoplight": {
              "id": "g80s05pbxm7bq"
            },
            "description": "Host user uri",
            "example": "https://api.calendly.com/users/AAAAAAAAAAAAAAAA",
            "format": "uri"
          },
          "co_hosts": {
            "type": "array",
            "x-stoplight": {
              "id": "smhocxmji8deh"
            },
            "maxItems": 9,
            "description": "Collection of meeting co-host(s) user URIs",
            "items": {
              "x-stoplight": {
                "id": "0nqgfaej6nlv1"
              },
              "type": "string",
              "format": "uri"
            }
          },
          "duration": {
            "type": "number",
            "x-stoplight": {
              "id": "r2qqy76sh5ikv"
            },
            "maximum": 720,
            "description": "Duration of meeting in minutes"
          },
          "timezone": {
            "type": "string",
            "x-stoplight": {
              "id": "hsu3tz8o7ykos"
            },
            "description": "Time zone used for meeting. Defaults to host's time zone.",
            "example": "America/New_York"
          },
          "date_setting": {
            "x-stoplight": {
              "id": "hskuoq7f14ynp"
            },
            "oneOf": [
              {
                "$ref": "./models/date_setting/DateRange.yaml"
              },
              {
                "$ref": "./models/date_setting/DaysInFuture.yaml"
              },
              {
                "$ref": "./models/date_setting/Spots.yaml"
              }
            ]
          },
          "location": {
            "oneOf": [
              {
                "$ref": "./models/adhoc-locations/CustomLocation.yaml"
              },
              {
                "$ref": "./models/adhoc-locations/GoogleConference.yaml"
              },
              {
                "$ref": "./models/adhoc-locations/GotoMeetingConference.yaml"
              },
              {
                "$ref": "./models/adhoc-locations/InPersonMeeting.yaml"
              },
              {
                "$ref": "./models/adhoc-locations/InboundCall.yaml"
              },
              {
                "$ref": "./models/adhoc-locations/InviteeSpecifiedLocation.yaml"
              },
              {
                "$ref": "./models/adhoc-locations/MicrosoftTeamsConference.yaml"
              },
              {
                "$ref": "./models/adhoc-locations/OutboundCall.yaml"
              },
              {
                "$ref": "./models/adhoc-locations/WebExConference.yaml"
              },
              {
                "$ref": "./models/adhoc-locations/ZoomConference.yaml"
              }
            ]
          }
        },
        "required": [
          "name",
          "host",
          "duration",
          "date_setting"
        ]
      },
      "examples": {
        "Example: date_range": {
          "value": {
            "name": "My Meeting",
            "host": "https://api.calendly.com/users/AAAAAAAAAAAAAAAA",
            "co_hosts": [
              "https://api.calendly.com/users/BBBBBBBBBBBBBBBB",
              "https://api.calendly.com/users/CCCCCCCCCCCCCCCC"
            ],
            "duration": 30,
            "timezone": "string",
            "date_setting": {
              "type": "date_range",
              "start_date": "2020-01-07",
              "end_date": "2020-01-09"
            },
            "location": {
              "kind": "physical",
              "location": "Main Office",
              "additonal_info": "string"
            }
          }
        },
        "Example: days_in_future": {
          "value": {
            "name": "My Meeting",
            "host": "https://api.calendly.com/users/AAAAAAAAAAAAAAAA",
            "co_hosts": [
              "https://api.calendly.com/users/BBBBBBBBBBBBBBBB",
              "https://api.calendly.com/users/CCCCCCCCCCCCCCCC"
            ],
            "duration": 30,
            "timezone": "string",
            "date_setting": {
              "type": "days_in_future",
              "days": 60,
              "only_weekdays": false
            },
            "location": {
              "kind": "physical",
              "location": "Main Office",
              "additonal_info": "string"
            }
          }
        },
        "Example: spots": {
          "value": {
            "name": "My Meeting",
            "host": "https://api.calendly.com/users/AAAAAAAAAAAAAAAA",
            "co_hosts": [
              "http://example.com"
            ],
            "duration": 720,
            "timezone": "America/New_York",
            "date_setting": {
              "type": "spots",
              "spots": [
                {
                  "start_time": "2020-01-02T11:30:00.000000Z",
                  "end_time": "2020-01-02T12:00:00.000000Z"
                }
              ]
            },
            "location": {
              "kind": "custom",
              "location": "string"
            }
          }
        }
      }
    }
  },
  "description": "Parameters for creating One-Off Event Type"
}
