http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "required": [
          "availability_rule"
        ],
        "properties": {
          "availability_rule": {
            "type": "object",
            "x-stoplight": {
              "id": "1k57u40qbjcoe"
            },
            "required": [
              "timezone"
            ],
            "properties": {
              "timezone": {
                "type": "string",
                "x-stoplight": {
                  "id": "t7ppm5kfbx3t3"
                },
                "description": "The timezone for which this Event Type Availability Schedule is originated in."
              },
              "rules": {
                "type": "array",
                "description": "The rules for an availability schedule.<br>  <span style=\"color:red\">Warning: </span>Updating rules will overwrite all existing rules for the event type. Use the GET endpoint to first retrieve the existing rules and then pass the modified rules to the rules object.",
                "x-stoplight": {
                  "id": "08tupds2jfoeg"
                },
                "items": {
                  "title": "Availability Rule",
                  "x-stoplight": {
                    "id": "a1hurebl7s3c6"
                  },
                  "type": "object",
                  "x-examples": {
                    "wday": {
                      "type": "wday",
                      "intervals": [
                        {
                          "from": "08:30",
                          "to": "09:30"
                        }
                      ],
                      "wday": "sunday"
                    },
                    "date": {
                      "type": "date",
                      "intervals": [
                        {
                          "from": "08:30",
                          "to": "09:30"
                        }
                      ],
                      "date": "2028-12-31"
                    }
                  },
                  "x-internal": false,
                  "description": "The rules for an availability schedule.",
                  "x-tags": [
                    "Availability"
                  ],
                  "required": [
                    "type",
                    "intervals"
                  ],
                  "properties": {
                    "type": {
                      "type": "string",
                      "enum": [
                        "wday",
                        "date"
                      ],
                      "example": "wday",
                      "description": "The type of this Availability Rule; can be \"wday\" or a specific \"date\"."
                    },
                    "intervals": {
                      "type": "array",
                      "description": "The intervals to be applied to this Rule. Each interval represents when booking a meeting is allowed. If the interval array is empty, then there is no booking availability for that day. Time is in 24h format (i.e. \"17:30\") and local to the timezone in the Availability Schedule.",
                      "items": {
                        "type": "object",
                        "properties": {
                          "from": {
                            "type": "string",
                            "description": "Format: `\"hh:mm\"`",
                            "pattern": "(\\d\\d):(\\d\\d)",
                            "example": "08:30"
                          },
                          "to": {
                            "type": "string",
                            "description": "Format: `\"hh:mm\"`",
                            "pattern": "(\\d\\d):(\\d\\d)",
                            "example": "09:30"
                          }
                        }
                      }
                    },
                    "wday": {
                      "type": "string",
                      "enum": [
                        "sunday",
                        "monday",
                        "tuesday",
                        "wednesday",
                        "thursday",
                        "friday",
                        "saturday"
                      ],
                      "description": "The day of the week for which this Rule should be applied to."
                    },
                    "date": {
                      "type": "string",
                      "example": "2022-12-31",
                      "pattern": "^\\d{4}\\-(0?[1-9]|1[012])\\-(0?[1-9]|[12][0-9]|3[01])$",
                      "description": "A specific date in the future that this should be applied to (i.e. \"2030-12-31\")."
                    }
                  }
                }
              },
              "user": {
                "type": "string",
                "x-stoplight": {
                  "id": "mpkhqhn87jrdb"
                },
                "format": "uri",
                "example": "https://api.calendly.com/users/abc123",
                "description": "Required when an admin or org owner is making the call to update a specific users availability schedule"
              }
            }
          },
          "availability_setting": {
            "x-stoplight": {
              "id": "7v3rsooj36r04"
            },
            "enum": [
              "host"
            ],
            "default": "host",
            "description": "By default every host on the Event Type shares an identical schedule.\n"
          }
        }
      },
      "examples": {
        "Updating Timezone": {
          "value": {
            "availability_rule": {
              "timezone": "America/Chicago"
            }
          }
        },
        "Updating Rules to ONLY have availability on Sunday": {
          "value": {
            "availability_rule": {
              "timezone": "America/New_York",
              "rules": [
                {
                  "type": "wday",
                  "intervals": [
                    {
                      "from": "08:30",
                      "to": "09:30"
                    }
                  ],
                  "wday": "sunday"
                }
              ]
            }
          }
        },
        "Updating Rules to have availability every day": {
          "value": {
            "availability_setting": "host",
            "availability_rule": {
              "timezone": "string",
              "rules": [
                {
                  "type": "wday",
                  "intervals": [
                    {
                      "from": "08:30",
                      "to": "09:30"
                    }
                  ],
                  "wday": "sunday"
                },
                {
                  "type": "wday",
                  "intervals": [
                    {
                      "from": "08:30",
                      "to": "09:30"
                    }
                  ],
                  "wday": "monday"
                },
                {
                  "type": "wday",
                  "intervals": [
                    {
                      "from": "08:30",
                      "to": "09:30"
                    }
                  ],
                  "wday": "tuesday"
                },
                {
                  "type": "wday",
                  "intervals": [
                    {
                      "from": "08:30",
                      "to": "09:30"
                    }
                  ],
                  "wday": "wednesday"
                },
                {
                  "type": "wday",
                  "intervals": [
                    {
                      "from": "08:30",
                      "to": "09:30"
                    }
                  ],
                  "wday": "thursday"
                },
                {
                  "type": "wday",
                  "intervals": [
                    {
                      "from": "08:30",
                      "to": "09:30"
                    }
                  ],
                  "wday": "friday"
                },
                {
                  "type": "wday",
                  "intervals": [
                    {
                      "from": "08:30",
                      "to": "09:30"
                    }
                  ],
                  "wday": "saturday"
                }
              ]
            }
          }
        },
        "An admin or org owner updating a user": {
          "value": {
            "availability_rule": {
              "timezone": "string",
              "rules": [
                {
                  "type": "wday",
                  "intervals": [
                    {
                      "from": "08:30",
                      "to": "09:30"
                    }
                  ],
                  "wday": "sunday",
                  "date": "2022-12-31"
                }
              ],
              "user": "https://api.calendly.com/users/abc123"
            },
            "availability_setting": "host"
          }
        }
      }
    }
  }
}
