http_endpoint.request_body_spec:
{
  "description": "Your request is a free form object representing the contents of your collection. This request replaces the contents of the collection entirely, so include all contents that you want to remain in the collection\u2014whether they change or not.",
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "additionalProperties": {
          "x-additionalPropertiesName": "collection contents"
        }
      },
      "example": {
        "eventName": "christmas",
        "eventDate": "2021-12-25T12:00:00.000Z",
        "presents": {
          "something_you_want": "toys",
          "something_you_need": "socks",
          "total": 2
        }
      }
    }
  }
}
