http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "description": "The IDs of people you want to add to the segment.",
        "required": [
          "ids"
        ],
        "properties": {
          "ids": {
            "type": "array",
            "description": "The customer IDs you want to add to the segment. The type of value you pass in the array corresponds to the `id_type` query parameter\u2014`id`, `email`, or `cio_id`. Entries in the array that don't match the `id_type` are ignored.",
            "maxItems": 1000,
            "minItems": 1,
            "items": {
              "description": "The customer IDs you want to add to the segment.",
              "type": "string"
            }
          }
        }
      },
      "example": {
        "ids": [
          "customer_id1",
          "customer_id2"
        ]
      }
    }
  }
}
