http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Team link create request",
        "properties": {
          "data": {
            "description": "Team link create",
            "properties": {
              "attributes": {
                "description": "Team link attributes",
                "properties": {
                  "label": {
                    "description": "The link's label",
                    "example": "Link label",
                    "maxLength": 256,
                    "type": "string"
                  },
                  "position": {
                    "description": "The link's position, used to sort links for the team",
                    "format": "int32",
                    "maximum": 2147483647,
                    "type": "integer"
                  },
                  "team_id": {
                    "description": "ID of the team the link is associated with",
                    "readOnly": true,
                    "type": "string"
                  },
                  "url": {
                    "description": "The URL for the link",
                    "example": "https://example.com",
                    "type": "string"
                  }
                },
                "required": [
                  "label",
                  "url"
                ],
                "type": "object"
              },
              "type": {
                "default": "team_links",
                "description": "Team link type",
                "enum": [
                  "team_links"
                ],
                "example": "team_links",
                "type": "string",
                "x-enum-varnames": [
                  "TEAM_LINKS"
                ]
              }
            },
            "required": [
              "attributes",
              "type"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
