http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "The object sent with the request to retrieve aggregation buckets of RUM events from your organization.",
        "properties": {
          "compute": {
            "description": "The list of metrics or timeseries to compute for the retrieved buckets.",
            "items": {
              "description": "A compute rule to compute metrics or timeseries.",
              "properties": {
                "aggregation": {
                  "description": "An aggregation function.",
                  "enum": [
                    "count",
                    "cardinality",
                    "pc75",
                    "pc90",
                    "pc95",
                    "pc98",
                    "pc99",
                    "sum",
                    "min",
                    "max",
                    "avg",
                    "median"
                  ],
                  "example": "pc90",
                  "type": "string",
                  "x-enum-varnames": [
                    "COUNT",
                    "CARDINALITY",
                    "PERCENTILE_75",
                    "PERCENTILE_90",
                    "PERCENTILE_95",
                    "PERCENTILE_98",
                    "PERCENTILE_99",
                    "SUM",
                    "MIN",
                    "MAX",
                    "AVG",
                    "MEDIAN"
                  ]
                },
                "interval": {
                  "description": "The time buckets' size (only used for type=timeseries)\nDefaults to a resolution of 150 points.",
                  "example": "5m",
                  "type": "string"
                },
                "metric": {
                  "description": "The metric to use.",
                  "example": "@duration",
                  "type": "string"
                },
                "type": {
                  "default": "total",
                  "description": "The type of compute.",
                  "enum": [
                    "timeseries",
                    "total"
                  ],
                  "type": "string",
                  "x-enum-varnames": [
                    "TIMESERIES",
                    "TOTAL"
                  ]
                }
              },
              "required": [
                "aggregation"
              ],
              "type": "object"
            },
            "type": "array"
          },
          "filter": {
            "description": "The search and filter query settings.",
            "properties": {
              "from": {
                "default": "now-15m",
                "description": "The minimum time for the requested events; supports date (in [ISO 8601](https://www.w3.org/TR/NOTE-datetime) format with full date, hours, minutes, and the `Z` UTC indicator - seconds and fractional seconds are optional), math, and regular timestamps (in milliseconds).",
                "example": "now-15m",
                "type": "string"
              },
              "query": {
                "default": "*",
                "description": "The search query following the RUM search syntax.",
                "example": "@type:session AND @session.type:user",
                "type": "string"
              },
              "to": {
                "default": "now",
                "description": "The maximum time for the requested events; supports date (in [ISO 8601](https://www.w3.org/TR/NOTE-datetime) format with full date, hours, minutes, and the `Z` UTC indicator - seconds and fractional seconds are optional), math, and regular timestamps (in milliseconds).",
                "example": "now",
                "type": "string"
              }
            },
            "type": "object"
          },
          "group_by": {
            "description": "The rules for the group by.",
            "items": {
              "description": "A group-by rule.",
              "properties": {
                "facet": {
                  "description": "The name of the facet to use (required).",
                  "example": "@view.time_spent",
                  "type": "string"
                },
                "histogram": {
                  "description": "Used to perform a histogram computation (only for measure facets).\nNote: At most 100 buckets are allowed, the number of buckets is (max - min)/interval.",
                  "properties": {
                    "interval": {
                      "description": "The bin size of the histogram buckets.",
                      "example": 10,
                      "format": "double",
                      "type": "number"
                    },
                    "max": {
                      "description": "The maximum value for the measure used in the histogram\n(values greater than this one are filtered out).",
                      "example": 100,
                      "format": "double",
                      "type": "number"
                    },
                    "min": {
                      "description": "The minimum value for the measure used in the histogram\n(values smaller than this one are filtered out).",
                      "example": 50,
                      "format": "double",
                      "type": "number"
                    }
                  },
                  "required": [
                    "interval",
                    "min",
                    "max"
                  ],
                  "type": "object"
                },
                "limit": {
                  "default": 10,
                  "description": "The maximum buckets to return for this group-by.",
                  "format": "int64",
                  "type": "integer"
                },
                "missing": {
                  "description": "The value to use for logs that don't have the facet used to group by.",
                  "oneOf": [
                    {
                      "description": "The missing value to use if there is string valued facet.",
                      "type": "string"
                    },
                    {
                      "description": "The missing value to use if there is a number valued facet.",
                      "format": "double",
                      "type": "number"
                    }
                  ]
                },
                "sort": {
                  "description": "A sort rule.",
                  "example": {
                    "aggregation": "count",
                    "order": "asc"
                  },
                  "properties": {
                    "aggregation": {
                      "description": "An aggregation function.",
                      "enum": [
                        "count",
                        "cardinality",
                        "pc75",
                        "pc90",
                        "pc95",
                        "pc98",
                        "pc99",
                        "sum",
                        "min",
                        "max",
                        "avg",
                        "median"
                      ],
                      "example": "pc90",
                      "type": "string",
                      "x-enum-varnames": [
                        "COUNT",
                        "CARDINALITY",
                        "PERCENTILE_75",
                        "PERCENTILE_90",
                        "PERCENTILE_95",
                        "PERCENTILE_98",
                        "PERCENTILE_99",
                        "SUM",
                        "MIN",
                        "MAX",
                        "AVG",
                        "MEDIAN"
                      ]
                    },
                    "metric": {
                      "description": "The metric to sort by (only used for `type=measure`).",
                      "example": "@duration",
                      "type": "string"
                    },
                    "order": {
                      "description": "The order to use, ascending or descending.",
                      "enum": [
                        "asc",
                        "desc"
                      ],
                      "example": "asc",
                      "type": "string",
                      "x-enum-varnames": [
                        "ASCENDING",
                        "DESCENDING"
                      ]
                    },
                    "type": {
                      "default": "alphabetical",
                      "description": "The type of sorting algorithm.",
                      "enum": [
                        "alphabetical",
                        "measure"
                      ],
                      "type": "string",
                      "x-enum-varnames": [
                        "ALPHABETICAL",
                        "MEASURE"
                      ]
                    }
                  },
                  "type": "object"
                },
                "total": {
                  "default": false,
                  "description": "A resulting object to put the given computes in over all the matching records.",
                  "oneOf": [
                    {
                      "description": "If set to true, creates an additional bucket labeled \"$facet_total\".",
                      "type": "boolean"
                    },
                    {
                      "description": "A string to use as the key value for the total bucket.",
                      "type": "string"
                    },
                    {
                      "description": "A number to use as the key value for the total bucket.",
                      "format": "double",
                      "type": "number"
                    }
                  ]
                }
              },
              "required": [
                "facet"
              ],
              "type": "object"
            },
            "type": "array"
          },
          "options": {
            "description": "Global query options that are used during the query.\nNote: Only supply timezone or time offset, not both. Otherwise, the query fails.",
            "properties": {
              "time_offset": {
                "description": "The time offset (in seconds) to apply to the query.",
                "format": "int64",
                "type": "integer"
              },
              "timezone": {
                "default": "UTC",
                "description": "The timezone can be specified as GMT, UTC, an offset from UTC (like UTC+1), or as a Timezone Database identifier (like America/New_York).",
                "example": "GMT",
                "type": "string"
              }
            },
            "type": "object"
          },
          "page": {
            "description": "Paging attributes for listing events.",
            "properties": {
              "cursor": {
                "description": "List following results with a cursor provided in the previous query.",
                "example": "eyJzdGFydEF0IjoiQVFBQUFYS2tMS3pPbm40NGV3QUFBQUJCV0V0clRFdDZVbG8zY3pCRmNsbHJiVmxDWlEifQ==",
                "type": "string"
              },
              "limit": {
                "default": 10,
                "description": "Maximum number of events in the response.",
                "example": 25,
                "format": "int32",
                "maximum": 1000,
                "type": "integer"
              }
            },
            "type": "object"
          }
        },
        "type": "object"
      }
    }
  },
  "required": true
}
