http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request to delete items from a datastore.",
        "properties": {
          "data": {
            "description": "Data wrapper containing the data needed to delete items from a datastore.",
            "properties": {
              "attributes": {
                "description": "Attributes of request data to delete items from a datastore.",
                "properties": {
                  "item_keys": {
                    "description": "List of primary keys identifying items to delete from datastore. Up to 100 items can be deleted in a single request.",
                    "items": {
                      "type": "string"
                    },
                    "maxItems": 100,
                    "type": "array"
                  }
                },
                "type": "object"
              },
              "id": {
                "description": "ID for the datastore of the items to delete.",
                "type": "string"
              },
              "type": {
                "default": "items",
                "description": "Items resource type.",
                "enum": [
                  "items"
                ],
                "example": "items",
                "type": "string",
                "x-enum-varnames": [
                  "ITEMS"
                ]
              }
            },
            "required": [
              "type"
            ],
            "type": "object"
          }
        },
        "type": "object"
      }
    }
  },
  "required": true
}
