http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request to insert multiple items into a datastore in a single operation.",
        "properties": {
          "data": {
            "description": "Data wrapper containing the items to insert and their configuration for the bulk insert operation.",
            "properties": {
              "attributes": {
                "description": "Configuration for bulk inserting multiple items into a datastore.",
                "properties": {
                  "conflict_mode": {
                    "description": "How to handle conflicts when inserting items that already exist in the datastore.",
                    "enum": [
                      "fail_on_conflict",
                      "overwrite_on_conflict"
                    ],
                    "example": "overwrite_on_conflict",
                    "type": "string",
                    "x-enum-varnames": [
                      "FAIL_ON_CONFLICT",
                      "OVERWRITE_ON_CONFLICT"
                    ]
                  },
                  "values": {
                    "description": "An array of items to add to the datastore, where each item is a set of key-value pairs representing the item's data. Up to 100 items can be updated in a single request.",
                    "example": [
                      {
                        "data": "example data",
                        "key": "value"
                      },
                      {
                        "data": "example data2",
                        "key": "value2"
                      }
                    ],
                    "items": {
                      "additionalProperties": {},
                      "description": "A single item's data as key-value pairs. Key names cannot exceed 63 characters.",
                      "type": "object"
                    },
                    "maxItems": 100,
                    "type": "array"
                  }
                },
                "required": [
                  "values"
                ],
                "type": "object"
              },
              "type": {
                "default": "items",
                "description": "The resource type for datastore items.",
                "enum": [
                  "items"
                ],
                "example": "items",
                "type": "string",
                "x-enum-varnames": [
                  "ITEMS"
                ]
              }
            },
            "required": [
              "type"
            ],
            "type": "object"
          }
        },
        "type": "object"
      }
    }
  },
  "required": true
}
