                    "x-enum-varnames": [
                      "INFO",
                      "LOW",
                      "MEDIUM",
                      "HIGH",
                      "CRITICAL"
                    ]
                  }
                },
                "required": [
                  "jobResultIds",
                  "signalSeverity",
                  "signalMessage",
                  "notifications"
                ],
                "type": "object"
              },
              "type": {
                "description": "Type of payload.",
                "enum": [
                  "historicalDetectionsJobResultSignalConversion"
                ],
                "type": "string",
                "x-enum-varnames": [
                  "HISTORICALDETECTIONSJOBRESULTSIGNALCONVERSION"
                ]
              }
            },
            "type": "object"
          }
        },
        "type": "object"
      }
    }
  },
  "required": true
}
