http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request object that includes the metric that you would like to configure tags for.",
        "properties": {
          "data": {
            "description": "Object for a single metric to be configure tags on.",
            "example": {
              "attributes": {
                "include_percentiles": false,
                "metric_type": "distribution",
                "tags": [
                  "app",
                  "datacenter"
                ]
              },
              "id": "http.endpoint.request",
              "type": "manage_tags"
            },
            "properties": {
              "attributes": {
                "description": "Object containing the definition of a metric tag configuration to be created.",
                "properties": {
                  "aggregations": {
                    "description": "Deprecated. You no longer need to configure specific time and space aggregations for Metrics Without Limits.",
                    "example": [
                      {
                        "space": "sum",
                        "time": "sum"
                      },
                      {
                        "space": "sum",
                        "time": "count"
                      }
                    ],
                    "items": {
                      "description": "A time and space aggregation combination for use in query.",
                      "example": {
                        "space": "sum",
                        "time": "sum"
                      },
                      "properties": {
                        "space": {
                          "description": "A space aggregation for use in query.",
                          "enum": [
                            "avg",
                            "max",
                            "min",
                            "sum"
                          ],
                          "example": "sum",
                          "type": "string",
                          "x-enum-varnames": [
                            "AVG",
                            "MAX",
                            "MIN",
                            "SUM"
                          ]
                        },
                        "time": {
                          "description": "A time aggregation for use in query.",
                          "enum": [
                            "avg",
                            "count",
                            "max",
                            "min",
                            "sum"
                          ],
                          "example": "sum",
                          "type": "string",
                          "x-enum-varnames": [
                            "AVG",
                            "COUNT",
                            "MAX",
                            "MIN",
                            "SUM"
                          ]
                        }
                      },
                      "required": [
                        "time",
                        "space"
                      ],
                      "type": "object"
                    },
                    "type": "array"
                  },
                  "exclude_tags_mode": {
                    "description": "When set to true, the configuration will exclude the configured tags and include any other submitted tags.\nWhen set to false, the configuration will include the configured tags and exclude any other submitted tags.\nDefaults to false. Requires `tags` property.",
                    "type": "boolean"
                  },
                  "include_percentiles": {
                    "description": "Toggle to include/exclude percentiles for a distribution metric.\nDefaults to false. Can only be applied to metrics that have a `metric_type` of `distribution`.",
                    "example": true,
                    "type": "boolean"
                  },
                  "metric_type": {
                    "default": "gauge",
                    "description": "The metric's type.",
                    "enum": [
                      "gauge",
                      "count",
                      "rate",
                      "distribution"
                    ],
                    "example": "count",
                    "type": "string",
                    "x-enum-varnames": [
                      "GAUGE",
                      "COUNT",
                      "RATE",
                      "DISTRIBUTION"
                    ]
                  },
                  "tags": {
                    "default": [],
                    "description": "A list of tag keys that will be queryable for your metric.",
                    "example": [
                      "app",
                      "datacenter"
                    ],
                    "items": {
                      "description": "Tag keys to group by.",
                      "type": "string"
                    },
                    "type": "array"
                  }
                },
                "required": [
                  "tags",
                  "metric_type"
                ],
                "type": "object"
              },
              "id": {
                "description": "The metric name for this resource.",
                "example": "test.metric.latency",
                "type": "string"
              },
              "type": {
                "default": "manage_tags",
                "description": "The metric tag configuration resource type.",
                "enum": [
                  "manage_tags"
                ],
                "example": "manage_tags",
                "type": "string",
                "x-enum-varnames": [
                  "MANAGE_TAGS"
                ]
              }
            },
            "required": [
              "id",
              "type"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
