http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request to create a new datastore with specified configuration and metadata.",
        "properties": {
          "data": {
            "description": "Data wrapper containing the configuration needed to create a new datastore.",
            "properties": {
              "attributes": {
                "description": "Configuration and metadata to create a new datastore.",
                "properties": {
                  "description": {
                    "description": "A human-readable description about the datastore.",
                    "type": "string"
                  },
                  "name": {
                    "description": "The display name for the new datastore.",
                    "example": "datastore-name",
                    "type": "string"
                  },
                  "org_access": {
                    "description": "The organization access level for the datastore. For example, 'contributor'.",
                    "enum": [
                      "contributor",
                      "viewer",
                      "manager"
                    ],
                    "type": "string",
                    "x-enum-varnames": [
                      "CONTRIBUTOR",
                      "VIEWER",
                      "MANAGER"
                    ]
                  },
                  "primary_column_name": {
                    "description": "The name of the primary key column for this datastore. Primary column names:\n  - Must abide by both [PostgreSQL naming conventions](https://www.postgresql.org/docs/7.0/syntax525.htm)\n  - Cannot exceed 63 characters",
                    "example": "",
                    "maxLength": 63,
                    "type": "string"
                  },
                  "primary_key_generation_strategy": {
                    "description": "Can be set to `uuid` to automatically generate primary keys when new items are added. Default value is `none`, which requires you to supply a primary key for each new item.",
                    "enum": [
                      "none",
                      "uuid"
                    ],
                    "type": "string",
                    "x-enum-varnames": [
                      "NONE",
                      "UUID"
                    ]
                  }
                },
                "required": [
                  "name",
                  "primary_column_name"
                ],
                "type": "object"
              },
              "id": {
                "description": "Optional ID for the new datastore. If not provided, one will be generated automatically.",
                "type": "string"
              },
              "type": {
                "default": "datastores",
                "description": "The resource type for datastores.",
                "enum": [
                  "datastores"
                ],
                "example": "datastores",
                "type": "string",
                "x-enum-varnames": [
                  "DATASTORES"
                ]
              }
            },
            "required": [
              "type"
            ],
            "type": "object"
          }
        },
        "type": "object"
      }
    }
  },
  "required": true
}
