http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Represents a request to create a new escalation policy, including the policy data.",
        "example": {
          "data": {
            "attributes": {
              "name": "Escalation Policy 1",
              "resolve_page_on_policy_end": true,
              "retries": 2,
              "steps": [
                {
                  "assignment": "default",
                  "escalate_after_seconds": 3600,
                  "targets": [
                    {
                      "id": "00000000-aba1-0000-0000-000000000000",
                      "type": "users"
                    },
                    {
                      "id": "00000000-aba2-0000-0000-000000000000",
                      "type": "schedules"
                    },
                    {
                      "id": "00000000-aba3-0000-0000-000000000000",
                      "type": "teams"
                    }
                  ]
                },
                {
                  "assignment": "round-robin",
                  "escalate_after_seconds": 3600,
                  "targets": [
                    {
                      "id": "00000000-aba1-0000-0000-000000000000",
                      "type": "users"
                    },
                    {
                      "id": "00000000-abb1-0000-0000-000000000000",
                      "type": "users"
                    }
                  ]
                }
              ]
            },
            "relationships": {
              "teams": {
                "data": [
                  {
                    "id": "00000000-da3a-0000-0000-000000000000",
                    "type": "teams"
                  }
                ]
              }
            },
            "type": "policies"
          }
        },
        "properties": {
          "data": {
            "description": "Represents the data for creating an escalation policy, including its attributes, relationships, and resource type.",
            "properties": {
              "attributes": {
                "description": "Defines the attributes for creating an escalation policy, including its description, name, resolution behavior, retries, and steps.",
                "properties": {
                  "name": {
                    "description": "Specifies the name for the new escalation policy.",
                    "example": "On-Call Escalation Policy",
                    "type": "string"
                  },
                  "resolve_page_on_policy_end": {
                    "description": "Indicates whether the page is automatically resolved when the policy ends.",
                    "type": "boolean"
                  },
                  "retries": {
                    "description": "Specifies how many times the escalation sequence is retried if there is no response.",
                    "format": "int64",
                    "type": "integer"
                  },
                  "steps": {
                    "description": "A list of escalation steps, each defining assignment, escalation timeout, and targets for the new policy.",
                    "items": {
                      "description": "Defines a single escalation step within an escalation policy creation request. Contains assignment strategy, escalation timeout, and a list of targets.",
                      "properties": {
                        "assignment": {
                          "description": "Specifies how this escalation step will assign targets (example `default` or `round-robin`).",
                          "enum": [
                            "default",
                            "round-robin"
                          ],
                          "type": "string",
                          "x-enum-varnames": [
                            "DEFAULT",
                            "ROUND_ROBIN"
                          ]
                        },
                        "escalate_after_seconds": {
                          "description": "Defines how many seconds to wait before escalating to the next step.",
                          "example": 3600,
                          "format": "int64",
                          "type": "integer"
                        },
                        "targets": {
                          "description": "Specifies the collection of escalation targets for this step.",
                          "example": [
                            "users"
                          ],
                          "items": {
                            "description": "Defines a single escalation target within a step for an escalation policy creation request. Contains `id` and `type`.",
                            "properties": {
                              "id": {
                                "description": "Specifies the unique identifier for this target.",
                                "example": "00000000-aba1-0000-0000-000000000000",
                                "type": "string"
                              },
                              "type": {
                                "description": "Specifies the type of escalation target (example `users`, `schedules`, or `teams`).",
                                "enum": [
                                  "users",
                                  "schedules",
                                  "teams"
                                ],
                                "example": "users",
                                "type": "string",
                                "x-enum-varnames": [
                                  "USERS",
                                  "SCHEDULES",
                                  "TEAMS"
                                ]
                              }
                            },
                            "type": "object"
                          },
                          "type": "array"
                        }
                      },
                      "required": [
                        "targets"
                      ],
                      "type": "object"
                    },
                    "type": "array"
                  }
                },
                "required": [
                  "name",
                  "steps"
                ],
                "type": "object"
              },
              "relationships": {
                "description": "Represents relationships in an escalation policy creation request, including references to teams.",
                "properties": {
                  "teams": {
                    "description": "Associates teams with this schedule in a data structure.",
                    "properties": {
                      "data": {
                        "description": "An array of team references for this schedule.",
                        "items": {
                          "description": "Relates a team to this schedule, identified by `id` and `type` (must be `teams`).",
                          "properties": {
                            "id": {
                              "description": "The unique identifier of the team in this relationship.",
                              "example": "00000000-da3a-0000-0000-000000000000",
                              "type": "string"
                            },
                            "type": {
                              "default": "teams",
                              "description": "Teams resource type.",
                              "enum": [
                                "teams"
                              ],
                              "example": "teams",
                              "type": "string",
                              "x-enum-varnames": [
                                "TEAMS"
                              ]
                            }
                          },
                          "required": [
                            "type",
                            "id"
                          ],
                          "type": "object"
                        },
                        "type": "array"
                      }
                    },
                    "type": "object"
                  }
                },
                "type": "object"
              },
              "type": {
                "default": "policies",
                "description": "Indicates that the resource is of type `policies`.",
                "enum": [
                  "policies"
                ],
                "example": "policies",
                "type": "string",
                "x-enum-varnames": [
                  "POLICIES"
                ]
              }
            },
            "required": [
              "type",
              "attributes"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
