http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "The top-level request body for schedule creation, wrapping a `data` object.",
        "example": {
          "data": {
            "attributes": {
              "layers": [
                {
                  "effective_date": "2025-02-03T05:00:00Z",
                  "end_date": "2025-12-31T00:00:00Z",
                  "interval": {
                    "days": 1
                  },
                  "members": [
                    {
                      "user": {
                        "id": "00000000-aba1-0000-0000-000000000000"
                      }
                    }
                  ],
                  "name": "Layer 1",
                  "restrictions": [
                    {
                      "end_day": "friday",
                      "end_time": "17:00:00",
                      "start_day": "monday",
                      "start_time": "09:00:00"
                    }
                  ],
                  "rotation_start": "2025-02-01T00:00:00Z"
                }
              ],
              "name": "On-Call Schedule",
              "time_zone": "America/New_York"
            },
            "relationships": {
              "teams": {
                "data": [
                  {
                    "id": "00000000-da3a-0000-0000-000000000000",
                    "type": "teams"
                  }
                ]
              }
            },
            "type": "schedules"
          }
        },
        "properties": {
          "data": {
            "description": "The core data wrapper for creating a schedule, encompassing attributes, relationships, and the resource type.",
            "properties": {
              "attributes": {
                "description": "Describes the main attributes for creating a new schedule, including name, layers, and time zone.",
                "properties": {
                  "layers": {
                    "description": "The layers of On-Call coverage that define rotation intervals and restrictions.",
                    "items": {
                      "description": "Describes a schedule layer, including rotation intervals, members, restrictions, and timeline settings.",
                      "properties": {
                        "effective_date": {
                          "description": "The date/time when this layer becomes active (in ISO 8601).",
                          "example": "2025-01-01T00:00:00Z",
                          "format": "date-time",
                          "type": "string"
                        },
                        "end_date": {
                          "description": "The date/time after which this layer no longer applies (in ISO 8601).",
                          "format": "date-time",
                          "type": "string"
                        },
                        "interval": {
                          "description": "Defines how often the rotation repeats, using a combination of days and optional seconds. Should be at least 1 hour.",
                          "properties": {
                            "days": {
                              "description": "The number of days in each rotation cycle.",
                              "example": 1,
                              "format": "int32",
                              "maximum": 400,
                              "type": "integer"
                            },
                            "seconds": {
                              "description": "Any additional seconds for the rotation cycle (up to 30 days).",
                              "example": 300,
                              "format": "int64",
                              "maximum": 2592000,
                              "type": "integer"
                            }
                          },
                          "type": "object"
                        },
                        "members": {
                          "description": "A list of members who participate in this layer's rotation.",
                          "items": {
                            "description": "Defines a single member within a schedule layer, including the reference to the underlying user.",
                            "properties": {
                              "user": {
                                "description": "Identifies the user participating in this layer as a single object with an `id`.",
                                "properties": {
                                  "id": {
                                    "description": "The user's ID.",
                                    "example": "00000000-aba1-0000-0000-000000000000",
                                    "type": "string"
                                  }
                                },
                                "type": "object"
                              }
                            },
                            "type": "object"
                          },
                          "type": "array"
                        },
                        "name": {
                          "description": "The name of this layer.",
                          "example": "Primary On-Call Layer",
                          "type": "string"
                        },
                        "restrictions": {
                          "description": "Zero or more time-based restrictions (for example, only weekdays, during business hours).",
                          "items": {
                            "description": "Defines a single time restriction rule with start and end times and the applicable weekdays.",
                            "properties": {
                              "end_day": {
                                "description": "A day of the week.",
                                "enum": [
                                  "monday",
                                  "tuesday",
                                  "wednesday",
                                  "thursday",
                                  "friday",
                                  "saturday",
                                  "sunday"
                                ],
                                "type": "string",
                                "x-enum-varnames": [
                                  "MONDAY",
                                  "TUESDAY",
                                  "WEDNESDAY",
                                  "THURSDAY",
                                  "FRIDAY",
                                  "SATURDAY",
                                  "SUNDAY"
                                ]
                              },
                              "end_time": {
                                "description": "Specifies the ending time for this restriction.",
                                "type": "string"
                              },
                              "start_day": {
                                "description": "A day of the week.",
                                "enum": [
                                  "monday",
                                  "tuesday",
                                  "wednesday",
                                  "thursday",
                                  "friday",
                                  "saturday",
                                  "sunday"
                                ],
                                "type": "string",
                                "x-enum-varnames": [
                                  "MONDAY",
                                  "TUESDAY",
                                  "WEDNESDAY",
                                  "THURSDAY",
                                  "FRIDAY",
                                  "SATURDAY",
                                  "SUNDAY"
                                ]
                              },
                              "start_time": {
                                "description": "Specifies the starting time for this restriction.",
                                "type": "string"
                              }
                            },
                            "type": "object"
                          },
                          "type": "array"
                        },
                        "rotation_start": {
                          "description": "The date/time when the rotation for this layer starts (in ISO 8601).",
                          "example": "2025-01-01T00:00:00Z",
                          "format": "date-time",
                          "type": "string"
                        }
                      },
                      "required": [
                        "name",
                        "interval",
                        "rotation_start",
                        "effective_date",
                        "members"
                      ],
                      "type": "object"
                    },
                    "type": "array"
                  },
                  "name": {
                    "description": "A human-readable name for the new schedule.",
                    "example": "Team A On-Call",
                    "type": "string"
                  },
                  "time_zone": {
                    "description": "The time zone in which the schedule is defined.",
                    "example": "America/New_York",
                    "type": "string"
                  }
                },
                "required": [
                  "name",
                  "time_zone",
                  "layers"
                ],
                "type": "object"
              },
              "relationships": {
                "description": "Gathers relationship objects for the schedule creation request, including the teams to associate.",
                "properties": {
                  "teams": {
                    "description": "Associates teams with this schedule in a data structure.",
                    "properties": {
                      "data": {
                        "description": "An array of team references for this schedule.",
                        "items": {
                          "description": "Relates a team to this schedule, identified by `id` and `type` (must be `teams`).",
                          "properties": {
                            "id": {
                              "description": "The unique identifier of the team in this relationship.",
                              "example": "00000000-da3a-0000-0000-000000000000",
                              "type": "string"
                            },
                            "type": {
                              "default": "teams",
                              "description": "Teams resource type.",
                              "enum": [
                                "teams"
                              ],
                              "example": "teams",
                              "type": "string",
                              "x-enum-varnames": [
                                "TEAMS"
                              ]
                            }
                          },
                          "required": [
                            "type",
                            "id"
                          ],
                          "type": "object"
                        },
                        "type": "array"
                      }
                    },
                    "type": "object"
                  }
                },
                "type": "object"
              },
              "type": {
                "default": "schedules",
                "description": "Schedules resource type.",
                "enum": [
                  "schedules"
                ],
                "example": "schedules",
                "type": "string",
                "x-enum-varnames": [
                  "SCHEDULES"
                ]
              }
            },
            "required": [
              "type",
              "attributes"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
