http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request to create an org connection.",
        "properties": {
          "data": {
            "description": "Org connection creation data.",
            "properties": {
              "attributes": {
                "description": "Attributes for creating an org connection.",
                "properties": {
                  "connection_types": {
                    "description": "List of connection types to establish.",
                    "example": [
                      "logs"
                    ],
                    "items": {
                      "description": "Available connection types between organizations.",
                      "enum": [
                        "logs",
                        "metrics"
                      ],
                      "example": "logs",
                      "type": "string",
                      "x-enum-varnames": [
                        "LOGS",
                        "METRICS"
                      ]
                    },
                    "minItems": 1,
                    "type": "array"
                  }
                },
                "required": [
                  "connection_types"
                ],
                "type": "object"
              },
              "relationships": {
                "description": "Relationships for org connection creation.",
                "properties": {
                  "sink_org": {
                    "description": "Org relationship.",
                    "properties": {
                      "data": {
                        "description": "The definition of `OrgConnectionOrgRelationshipData` object.",
                        "properties": {
                          "id": {
                            "description": "Org UUID.",
                            "example": "f9ec96b0-8c8a-4b0a-9b0a-1b2c3d4e5f6a",
                            "type": "string"
                          },
                          "name": {
                            "description": "Org name.",
                            "example": "Example Org",
                            "type": "string"
                          },
                          "type": {
                            "description": "The type of the organization relationship.",
                            "enum": [
                              "orgs"
                            ],
                            "example": "orgs",
                            "type": "string",
                            "x-enum-varnames": [
                              "ORGS"
                            ]
                          }
                        },
                        "type": "object"
                      }
                    },
                    "type": "object"
                  }
                },
                "required": [
                  "sink_org"
                ],
                "type": "object"
              },
              "type": {
                "description": "Org connection type.",
                "enum": [
                  "org_connection"
                ],
                "example": "org_connection",
                "type": "string",
                "x-enum-varnames": [
                  "ORG_CONNECTION"
                ]
              }
            },
            "required": [
              "type",
              "attributes",
              "relationships"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
