http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Powerpacks are templated groups of dashboard widgets you can save from an existing dashboard and turn into reusable packs in the widget tray.",
        "properties": {
          "data": {
            "description": "Powerpack data object.",
            "properties": {
              "attributes": {
                "description": "Powerpack attribute object.",
                "properties": {
                  "description": {
                    "description": "Description of this powerpack.",
                    "example": "Powerpack for ABC",
                    "type": "string"
                  },
                  "group_widget": {
                    "description": "Powerpack group widget definition object.",
                    "properties": {
                      "definition": {
                        "description": "Powerpack group widget object.",
                        "properties": {
                          "layout_type": {
                            "description": "Layout type of widgets.",
                            "example": "ordered",
                            "type": "string"
                          },
                          "show_title": {
                            "description": "Boolean indicating whether powerpack group title should be visible or not.",
                            "example": true,
                            "type": "boolean"
                          },
                          "title": {
                            "description": "Name for the group widget.",
                            "example": "Sample Powerpack",
                            "type": "string"
                          },
                          "type": {
                            "description": "Type of widget, must be group.",
                            "example": "group",
                            "type": "string"
                          },
                          "widgets": {
                            "description": "Widgets inside the powerpack.",
                            "example": [
                              {
                                "definition": {
                                  "content": "example",
                                  "type": "note"
                                },
                                "layout": {
                                  "height": 5,
                                  "width": 10,
                                  "x": 0,
                                  "y": 0
                                }
                              }
                            ],
                            "items": {
                              "description": "Powerpack group widget definition of individual widgets.",
                              "properties": {
                                "definition": {
                                  "additionalProperties": {},
                                  "description": "Information about widget.",
                                  "example": {
                                    "definition": {
                                      "content": "example",
                                      "type": "note"
                                    }
                                  },
                                  "type": "object"
                                },
                                "layout": {
                                  "description": "Powerpack inner widget layout.",
                                  "properties": {
                                    "height": {
                                      "description": "The height of the widget. Should be a non-negative integer.",
                                      "example": 0,
                                      "format": "int64",
                                      "minimum": 0,
                                      "type": "integer"
                                    },
                                    "width": {
                                      "description": "The width of the widget. Should be a non-negative integer.",
                                      "example": 0,
                                      "format": "int64",
                                      "minimum": 0,
                                      "type": "integer"
                                    },
                                    "x": {
                                      "description": "The position of the widget on the x (horizontal) axis. Should be a non-negative integer.",
                                      "example": 0,
                                      "format": "int64",
                                      "minimum": 0,
                                      "type": "integer"
                                    },
                                    "y": {
                                      "description": "The position of the widget on the y (vertical) axis. Should be a non-negative integer.",
                                      "example": 0,
                                      "format": "int64",
                                      "minimum": 0,
                                      "type": "integer"
                                    }
                                  },
                                  "required": [
                                    "x",
                                    "y",
                                    "width",
                                    "height"
                                  ],
                                  "type": "object"
                                }
                              },
                              "required": [
                                "definition"
                              ],
                              "type": "object"
                            },
                            "type": "array"
                          }
                        },
                        "required": [
                          "widgets",
                          "layout_type",
                          "type"
                        ],
                        "type": "object"
                      },
                      "layout": {
                        "description": "Powerpack group widget layout.",
                        "properties": {
                          "height": {
                            "description": "The height of the widget. Should be a non-negative integer.",
                            "example": 0,
                            "format": "int64",
                            "minimum": 0,
                            "type": "integer"
                          },
                          "width": {
                            "description": "The width of the widget. Should be a non-negative integer.",
                            "example": 0,
                            "format": "int64",
                            "minimum": 0,
                            "type": "integer"
                          },
                          "x": {
                            "description": "The position of the widget on the x (horizontal) axis. Should be a non-negative integer.",
                            "example": 0,
                            "format": "int64",
                            "minimum": 0,
                            "type": "integer"
                          },
                          "y": {
                            "description": "The position of the widget on the y (vertical) axis. Should be a non-negative integer.",
                            "example": 0,
                            "format": "int64",
                            "minimum": 0,
                            "type": "integer"
                          }
                        },
                        "required": [
                          "x",
                          "y",
                          "width",
                          "height"
                        ],
                        "type": "object"
                      },
                      "live_span": {
                        "description": "The available timeframes depend on the widget you are using.",
                        "enum": [
                          "1m",
                          "5m",
                          "10m",
                          "15m",
                          "30m",
                          "1h",
                          "4h",
                          "1d",
                          "2d",
                          "1w",
                          "1mo",
                          "3mo",
                          "6mo",
                          "1y",
                          "alert"
                        ],
                        "example": "5m",
                        "type": "string",
                        "x-enum-varnames": [
                          "PAST_ONE_MINUTE",
                          "PAST_FIVE_MINUTES",
                          "PAST_TEN_MINUTES",
                          "PAST_FIFTEEN_MINUTES",
                          "PAST_THIRTY_MINUTES",
                          "PAST_ONE_HOUR",
                          "PAST_FOUR_HOURS",
                          "PAST_ONE_DAY",
                          "PAST_TWO_DAYS",
                          "PAST_ONE_WEEK",
                          "PAST_ONE_MONTH",
                          "PAST_THREE_MONTHS",
                          "PAST_SIX_MONTHS",
                          "PAST_ONE_YEAR",
                          "ALERT"
                        ]
                      }
                    },
                    "required": [
                      "definition"
                    ],
                    "type": "object"
                  },
                  "name": {
                    "description": "Name of the powerpack.",
                    "example": "Sample Powerpack",
                    "type": "string"
                  },
                  "tags": {
                    "description": "List of tags to identify this powerpack.",
                    "example": [
                      "tag:foo1"
                    ],
                    "items": {
                      "maxLength": 80,
                      "type": "string"
                    },
                    "maxItems": 8,
                    "type": "array"
                  },
                  "template_variables": {
                    "description": "List of template variables for this powerpack.",
                    "example": [
                      {
                        "defaults": [
                          "*"
                        ],
                        "name": "test"
                      }
                    ],
                    "items": {
                      "description": "Powerpack template variables.",
                      "properties": {
                        "available_values": {
                          "description": "The list of values that the template variable drop-down is limited to.",
                          "example": [
                            "my-host",
                            "host1",
                            "host2"
                          ],
                          "items": {
                            "description": "Template variable value.",
                            "type": "string"
                          },
                          "nullable": true,
                          "type": "array"
                        },
                        "defaults": {
                          "description": "One or many template variable default values within the saved view, which are unioned together using `OR` if more than one is specified.",
                          "items": {
                            "description": "One or many default values of the template variable.",
                            "minLength": 1,
                            "type": "string"
                          },
                          "type": "array"
                        },
                        "name": {
                          "description": "The name of the variable.",
                          "example": "datacenter",
                          "type": "string"
                        },
                        "prefix": {
                          "description": "The tag prefix associated with the variable. Only tags with this prefix appear in the variable drop-down.",
                          "example": "host",
                          "nullable": true,
                          "type": "string"
                        }
                      },
                      "required": [
                        "name"
                      ],
                      "type": "object"
                    },
                    "type": "array"
                  }
                },
                "required": [
                  "group_widget",
                  "name"
                ],
                "type": "object"
              },
              "id": {
                "description": "ID of the powerpack.",
                "type": "string"
              },
              "relationships": {
                "description": "Powerpack relationship object.",
                "properties": {
                  "author": {
                    "description": "Relationship to user.",
                    "properties": {
                      "data": {
                        "description": "Relationship to user object.",
                        "properties": {
                          "id": {
                            "description": "A unique identifier that represents the user.",
                            "example": "00000000-0000-0000-2345-000000000000",
                            "type": "string"
                          },
                          "type": {
                            "default": "users",
                            "description": "Users resource type.",
                            "enum": [
                              "users"
                            ],
                            "example": "users",
                            "type": "string",
                            "x-enum-varnames": [
                              "USERS"
                            ]
                          }
                        },
                        "required": [
                          "id",
                          "type"
                        ],
                        "type": "object"
                      }
                    },
                    "required": [
                      "data"
                    ],
                    "type": "object"
                  }
                },
                "type": "object"
              },
              "type": {
                "description": "Type of widget, must be powerpack.",
                "example": "powerpack",
                "type": "string"
              }
            },
            "type": "object"
          }
        },
        "type": "object"
      }
    }
  },
  "description": "Create a powerpack request body.",
  "required": true
}
