http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Case create request",
        "properties": {
          "data": {
            "description": "Case creation data",
            "properties": {
              "attributes": {
                "description": "Case creation attributes",
                "properties": {
                  "description": {
                    "description": "Description",
                    "type": "string"
                  },
                  "priority": {
                    "default": "NOT_DEFINED",
                    "description": "Case priority",
                    "enum": [
                      "NOT_DEFINED",
                      "P1",
                      "P2",
                      "P3",
                      "P4",
                      "P5"
                    ],
                    "example": "NOT_DEFINED",
                    "type": "string",
                    "x-enum-varnames": [
                      "NOT_DEFINED",
                      "P1",
                      "P2",
                      "P3",
                      "P4",
                      "P5"
                    ]
                  },
                  "title": {
                    "description": "Title",
                    "example": "Security breach investigation",
                    "type": "string"
                  },
                  "type_id": {
                    "description": "Case type UUID",
                    "example": "3b010bde-09ce-4449-b745-71dd5f861963",
                    "type": "string"
                  }
                },
                "required": [
                  "title",
                  "type_id"
                ],
                "type": "object"
              },
              "relationships": {
                "description": "Relationships formed with the case on creation",
                "properties": {
                  "assignee": {
                    "description": "Relationship to user.",
                    "nullable": true,
                    "properties": {
                      "data": {
                        "description": "Relationship to user object.",
                        "nullable": true,
                        "properties": {
                          "id": {
                            "description": "A unique identifier that represents the user.",
                            "example": "00000000-0000-0000-0000-000000000000",
                            "type": "string"
                          },
                          "type": {
                            "default": "user",
                            "description": "User resource type.",
                            "enum": [
                              "user"
                            ],
                            "example": "user",
                            "type": "string",
                            "x-enum-varnames": [
                              "USER"
                            ]
                          }
                        },
                        "required": [
                          "id",
                          "type"
                        ],
                        "type": "object"
                      }
                    },
                    "required": [
                      "data"
                    ],
                    "type": "object"
                  },
                  "project": {
                    "description": "Relationship to project",
                    "properties": {
                      "data": {
                        "description": "Relationship to project object",
                        "properties": {
                          "id": {
                            "description": "A unique identifier that represents the project",
                            "example": "e555e290-ed65-49bd-ae18-8acbfcf18db7",
                            "type": "string"
                          },
                          "type": {
                            "default": "project",
                            "description": "Project resource type",
                            "enum": [
                              "project"
                            ],
                            "example": "project",
                            "type": "string",
                            "x-enum-varnames": [
                              "PROJECT"
                            ]
                          }
                        },
                        "required": [
                          "id",
                          "type"
                        ],
                        "type": "object"
                      }
                    },
                    "required": [
                      "data"
                    ],
                    "type": "object"
                  }
                },
                "required": [
                  "project"
                ],
                "type": "object"
              },
              "type": {
                "default": "case",
                "description": "Case resource type",
                "enum": [
                  "case"
                ],
                "example": "case",
                "type": "string",
                "x-enum-varnames": [
                  "CASE"
                ]
              }
            },
            "required": [
              "attributes",
              "type"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "description": "Case payload",
  "required": true
}
