http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request to delete a specific item from a datastore by its primary key.",
        "properties": {
          "data": {
            "description": "Data wrapper containing the information needed to identify and delete a specific datastore item.",
            "properties": {
              "attributes": {
                "description": "Attributes specifying which datastore item to delete by its primary key.",
                "properties": {
                  "id": {
                    "description": "Optional unique identifier of the item to delete.",
                    "example": "a7656bcc-51d4-4884-adf7-4d0d9a3e0633",
                    "type": "string"
                  },
                  "item_key": {
                    "description": "The primary key value that identifies the item to delete. Cannot exceed 256 characters.",
                    "example": "primaryKey",
                    "maxLength": 256,
                    "type": "string"
                  }
                },
                "required": [
                  "item_key"
                ],
                "type": "object"
              },
              "type": {
                "default": "items",
                "description": "The resource type for datastore items.",
                "enum": [
                  "items"
                ],
                "example": "items",
                "type": "string",
                "x-enum-varnames": [
                  "ITEMS"
                ]
              }
            },
            "required": [
              "type"
            ],
            "type": "object"
          }
        },
        "type": "object"
      }
    }
  },
  "required": true
}
