http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "The list of RUM retention filter IDs along with their corresponding type to reorder.\nAll retention filter IDs should be included in the list created for a RUM application.",
        "properties": {
          "data": {
            "description": "A list of RUM retention filter IDs along with type.",
            "items": {
              "description": "The RUM retention filter data for ordering.",
              "properties": {
                "id": {
                  "description": "ID of retention filter in UUID.",
                  "example": "051601eb-54a0-abc0-03f9-cc02efa18892",
                  "type": "string"
                },
                "type": {
                  "default": "retention_filters",
                  "description": "The type of the resource. The value should always be retention_filters.",
                  "enum": [
                    "retention_filters"
                  ],
                  "example": "retention_filters",
                  "type": "string",
                  "x-enum-varnames": [
                    "RETENTION_FILTERS"
                  ]
                }
              },
              "required": [
                "id",
                "type"
              ],
              "type": "object"
            },
            "type": "array"
          }
        },
        "type": "object"
      }
    }
  },
  "description": "New definition of the RUM retention filter.",
  "required": true
}
