http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "A wrapper request around one scalar query to be executed.",
        "properties": {
          "data": {
            "description": "A single scalar query to be executed.",
            "properties": {
              "attributes": {
                "description": "The object describing a scalar formula request.",
                "properties": {
                  "formulas": {
                    "description": "List of formulas to be calculated and returned as responses.",
                    "items": {
                      "description": "A formula for calculation based on one or more queries.",
                      "properties": {
                        "formula": {
                          "description": "Formula string, referencing one or more queries with their name property.",
                          "example": "a+b",
                          "type": "string"
                        },
                        "limit": {
                          "description": "Message for specifying limits to the number of values returned by a query.\nThis limit is only for scalar queries and has no effect on timeseries queries.",
                          "properties": {
                            "count": {
                              "description": "The number of results to which to limit.",
                              "example": 10,
                              "format": "int32",
                              "maximum": 2147483647,
                              "type": "integer"
                            },
                            "order": {
                              "default": "desc",
                              "description": "Direction of sort.",
                              "enum": [
                                "asc",
                                "desc"
                              ],
                              "type": "string",
                              "x-enum-varnames": [
                                "ASC",
                                "DESC"
                              ]
                            }
                          },
                          "type": "object"
                        }
                      },
                      "required": [
                        "formula"
                      ],
                      "type": "object"
                    },
                    "type": "array"
                  },
                  "from": {
                    "description": "Start date (inclusive) of the query in milliseconds since the Unix epoch.",
                    "example": 1568899800000,
                    "format": "int64",
                    "type": "integer"
                  },
                  "queries": {
                    "description": "List of queries to be run and used as inputs to the formulas.",
                    "example": [
                      {
                        "aggregator": "avg",
                        "data_source": "metrics",
                        "query": "avg:system.cpu.user{*} by {env}"
                      }
                    ],
                    "items": {
                      "description": "An individual scalar query to one of the basic Datadog data sources.",
                      "example": {
                        "aggregator": "avg",
                        "data_source": "metrics",
                        "query": "avg:system.cpu.user{*} by {env}"
                      },
                      "oneOf": [
                        {
                          "description": "An individual scalar metrics query.",
                          "properties": {
                            "aggregator": {
                              "default": "avg",
                              "description": "The type of aggregation that can be performed on metrics-based queries.",
                              "enum": [
                                "avg",
                                "min",
                                "max",
                                "sum",
                                "last",
                                "percentile",
                                "mean",
                                "l2norm",
                                "area"
                              ],
                              "example": "avg",
                              "type": "string",
                              "x-enum-varnames": [
                                "AVG",
                                "MIN",
                                "MAX",
                                "SUM",
                                "LAST",
                                "PERCENTILE",
                                "MEAN",
                                "L2NORM",
                                "AREA"
                              ]
                            },
                            "data_source": {
                              "default": "metrics",
                              "description": "A data source that is powered by the Metrics platform.",
                              "enum": [
                                "metrics",
                                "cloud_cost"
                              ],
                              "example": "metrics",
                              "type": "string",
                              "x-enum-varnames": [
                                "METRICS",
                                "CLOUD_COST"
                              ]
                            },
                            "name": {
                              "description": "The variable name for use in formulas.",
                              "type": "string"
                            },
                            "query": {
                              "description": "A classic metrics query string.",
                              "example": "avg:system.cpu.user{*} by {env}",
                              "type": "string"
                            }
                          },
                          "required": [
                            "data_source",
                            "query",
                            "aggregator"
                          ],
                          "type": "object"
                        },
                        {
                          "description": "An individual scalar events query.",
                          "properties": {
                            "compute": {
                              "description": "The instructions for what to compute for this query.",
                              "properties": {
                                "aggregation": {
                                  "default": "count",
                                  "description": "The type of aggregation that can be performed on events-based queries.",
                                  "enum": [
                                    "count",
                                    "cardinality",
                                    "pc75",
                                    "pc90",
                                    "pc95",
                                    "pc98",
                                    "pc99",
                                    "sum",
                                    "min",
                                    "max",
                                    "avg"
                                  ],
                                  "example": "count",
                                  "type": "string",
                                  "x-enum-varnames": [
                                    "COUNT",
                                    "CARDINALITY",
                                    "PC75",
                                    "PC90",
                                    "PC95",
                                    "PC98",
                                    "PC99",
                                    "SUM",
                                    "MIN",
                                    "MAX",
                                    "AVG"
                                  ]
                                },
                                "interval": {
                                  "description": "Interval for compute in milliseconds.",
                                  "example": 60000,
                                  "format": "int64",
                                  "type": "integer"
                                },
                                "metric": {
                                  "description": "The \"measure\" attribute on which to perform the computation.",
                                  "type": "string"
                                }
                              },
                              "required": [
                                "aggregation"
                              ],
                              "type": "object"
                            },
                            "data_source": {
                              "default": "logs",
                              "description": "A data source that is powered by the Events Platform.",
                              "enum": [
                                "logs",
                                "rum",
                                "dora"
                              ],
                              "example": "logs",
                              "type": "string",
                              "x-enum-varnames": [
                                "LOGS",
                                "RUM",
                                "DORA"
                              ]
                            },
                            "group_by": {
                              "description": "The list of facets on which to split results.",
                              "items": {
                                "description": "A dimension on which to split a query's results.",
                                "properties": {
                                  "facet": {
                                    "description": "The facet by which to split groups.",
                                    "example": "@error.type",
                                    "type": "string"
                                  },
                                  "limit": {
                                    "default": 10,
                                    "description": "The maximum buckets to return for this group by. Note: at most 10000 buckets are allowed.\nIf grouping by multiple facets, the product of limits must not exceed 10000.",
                                    "example": 10,
                                    "format": "int32",
                                    "maximum": 10000,
                                    "type": "integer"
                                  },
                                  "sort": {
                                    "description": "The dimension by which to sort a query's results.",
                                    "properties": {
                                      "aggregation": {
                                        "default": "count",
                                        "description": "The type of aggregation that can be performed on events-based queries.",
                                        "enum": [
                                          "count",
                                          "cardinality",
                                          "pc75",
                                          "pc90",
                                          "pc95",
                                          "pc98",
                                          "pc99",
                                          "sum",
                                          "min",
                                          "max",
                                          "avg"
                                        ],
                                        "example": "count",
                                        "type": "string",
                                        "x-enum-varnames": [
                                          "COUNT",
                                          "CARDINALITY",
                                          "PC75",
                                          "PC90",
                                          "PC95",
                                          "PC98",
                                          "PC99",
                                          "SUM",
                                          "MIN",
                                          "MAX",
                                          "AVG"
                                        ]
                                      },
                                      "metric": {
                                        "description": "The metric's calculated value which should be used to define the sort order of a query's results.",
                                        "example": "@duration",
                                        "type": "string"
                                      },
                                      "order": {
                                        "default": "desc",
                                        "description": "Direction of sort.",
                                        "enum": [
                                          "asc",
                                          "desc"
                                        ],
                                        "type": "string",
                                        "x-enum-varnames": [
                                          "ASC",
                                          "DESC"
                                        ]
                                      },
                                      "type": {
                                        "description": "The type of sort to use on the calculated value.",
                                        "enum": [
                                          "alphabetical",
                                          "measure"
                                        ],
                                        "type": "string",
                                        "x-enum-varnames": [
                                          "ALPHABETICAL",
                                          "MEASURE"
                                        ]
                                      }
                                    },
                                    "required": [
                                      "aggregation"
                                    ],
                                    "type": "object"
                                  }
                                },
                                "required": [
                                  "facet"
                                ],
                                "type": "object"
                              },
                              "type": "array"
                            },
                            "indexes": {
                              "description": "The indexes in which to search.",
                              "example": [
                                "main"
                              ],
                              "items": {
                                "description": "The unique index name.",
                                "example": "main",
                                "type": "string"
                              },
                              "type": "array"
                            },
                            "name": {
                              "description": "The variable name for use in formulas.",
                              "type": "string"
                            },
                            "search": {
                              "description": "Configuration of the search/filter for an events query.",
                              "properties": {
                                "query": {
                                  "description": "The search/filter string for an events query.",
                                  "example": "status:warn service:foo",
                                  "type": "string"
                                }
                              },
                              "type": "object"
                            }
                          },
                          "required": [
                            "data_source",
                            "compute"
                          ],
                          "type": "object"
                        }
                      ]
                    },
                    "type": "array"
                  },
                  "to": {
                    "description": "End date (exclusive) of the query in milliseconds since the Unix epoch.",
                    "example": 1568923200000,
                    "format": "int64",
                    "type": "integer"
                  }
                },
                "required": [
                  "to",
                  "from",
                  "queries"
                ],
                "type": "object"
              },
              "type": {
                "default": "scalar_request",
                "description": "The type of the resource. The value should always be scalar_request.",
                "enum": [
                  "scalar_request"
                ],
                "example": "scalar_request",
                "type": "string",
                "x-enum-varnames": [
                  "SCALAR_REQUEST"
                ]
              }
            },
            "required": [
              "type",
              "attributes"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
