http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "The request for a flaky tests search.",
        "properties": {
          "data": {
            "description": "The JSON:API data for flaky tests search request.",
            "properties": {
              "attributes": {
                "description": "Attributes for the flaky tests search request.",
                "properties": {
                  "filter": {
                    "description": "Search filter settings.",
                    "properties": {
                      "query": {
                        "default": "*",
                        "description": "Search query following log syntax used to filter flaky tests, same as on Flaky Tests Management UI. The supported search keys are:\n- `flaky_test_state`\n- `flaky_test_category`\n- `@test.name`\n- `@test.suite`\n- `@test.module`\n- `@test.service`\n- `@git.repository.id_v2`\n- `@git.branch`\n- `@test.codeowners`\n- `env`",
                        "example": "flaky_test_state:active @git.repository.id_v2:\"github.com/datadog/shopist\"",
                        "type": "string"
                      }
                    },
                    "type": "object"
                  },
                  "page": {
                    "description": "Pagination attributes for listing flaky tests.",
                    "properties": {
                      "cursor": {
                        "description": "List following results with a cursor provided in the previous request.",
                        "example": "eyJzdGFydEF0IjoiQVFBQUFYS2tMS3pPbm40NGV3QUFBQUJCV0V0clRFdDZVbG8zY3pCRmNsbHJiVmxDWlEifQ==",
                        "type": "string"
                      },
                      "limit": {
                        "default": 10,
                        "description": "Maximum number of flaky tests in the response.",
                        "example": 25,
                        "format": "int64",
                        "maximum": 1000,
                        "minimum": 1,
                        "type": "integer"
                      }
                    },
                    "type": "object"
                  },
                  "sort": {
                    "description": "Parameter for sorting flaky test results. The default sort is by ascending Fully Qualified Name (FQN). The FQN is the concatenation of the test module, suite, and name.",
                    "enum": [
                      "fqn",
                      "-fqn",
                      "first_flaked",
                      "-first_flaked",
                      "last_flaked",
                      "-last_flaked",
                      "failure_rate",
                      "-failure_rate",
                      "pipelines_failed",
                      "-pipelines_failed",
                      "pipelines_duration_lost",
                      "-pipelines_duration_lost"
                    ],
                    "example": "failure_rate",
                    "type": "string",
                    "x-enum-varnames": [
                      "FQN_ASCENDING",
                      "FQN_DESCENDING",
                      "FIRST_FLAKED_ASCENDING",
                      "FIRST_FLAKED_DESCENDING",
                      "LAST_FLAKED_ASCENDING",
                      "LAST_FLAKED_DESCENDING",
                      "FAILURE_RATE_ASCENDING",
                      "FAILURE_RATE_DESCENDING",
                      "PIPELINES_FAILED_ASCENDING",
                      "PIPELINES_FAILED_DESCENDING",
                      "PIPELINES_DURATION_LOST_ASCENDING",
                      "PIPELINES_DURATION_LOST_DESCENDING"
                    ]
                  }
                },
                "type": "object"
              },
              "type": {
                "description": "The definition of `FlakyTestsSearchRequestDataType` object.",
                "enum": [
                  "search_flaky_tests_request"
                ],
                "type": "string",
                "x-enum-varnames": [
                  "SEARCH_FLAKY_TESTS_REQUEST"
                ]
              }
            },
            "type": "object"
          }
        },
        "type": "object"
      }
    }
  },
  "required": false
}
