http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Full request to trigger an On-Call Page.",
        "example": {
          "data": {
            "attributes": {
              "description": "Page details.",
              "tags": [
                "service:test"
              ],
              "target": {
                "identifier": "my-team",
                "type": "team_handle"
              },
              "title": "Page title",
              "urgency": "low"
            },
            "type": "pages"
          }
        },
        "properties": {
          "data": {
            "description": "The main request body, including attributes and resource type.",
            "properties": {
              "attributes": {
                "description": "Details about the On-Call Page you want to create.",
                "properties": {
                  "description": {
                    "description": "A short summary of the issue or context.",
                    "type": "string"
                  },
                  "tags": {
                    "description": "Tags to help categorize or filter the page.",
                    "items": {
                      "type": "string"
                    },
                    "type": "array"
                  },
                  "target": {
                    "description": "Information about the target to notify (such as a team or user).",
                    "properties": {
                      "identifier": {
                        "description": "Identifier for the target (for example, team handle or user ID).",
                        "type": "string"
                      },
                      "type": {
                        "description": "The kind of target, `team_id` | `team_handle` | `user_id`.",
                        "enum": [
                          "team_id",
                          "team_handle",
                          "user_id"
                        ],
                        "example": "team_id",
                        "type": "string",
                        "x-enum-varnames": [
                          "TEAM_ID",
                          "TEAM_HANDLE",
                          "USER_ID"
                        ]
                      }
                    },
                    "type": "object"
                  },
                  "title": {
                    "description": "The title of the page.",
                    "example": "Service: Test is down",
                    "type": "string"
                  },
                  "urgency": {
                    "default": "high",
                    "description": "On-Call Page urgency level.",
                    "enum": [
                      "low",
                      "high"
                    ],
                    "example": "high",
                    "type": "string",
                    "x-enum-varnames": [
                      "LOW",
                      "HIGH"
                    ]
                  }
                },
                "required": [
                  "target",
                  "title",
                  "urgency"
                ],
                "type": "object"
              },
              "type": {
                "default": "pages",
                "description": "The type of resource used when creating an On-Call Page.",
                "enum": [
                  "pages"
                ],
                "example": "pages",
                "type": "string",
                "x-enum-varnames": [
                  "PAGES"
                ]
              }
            },
            "required": [
              "type"
            ],
            "type": "object"
          }
        },
        "type": "object"
      }
    }
  },
  "required": true
}
