http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Case update custom attribute request",
        "properties": {
          "data": {
            "description": "Case update custom attribute",
            "properties": {
              "attributes": {
                "description": "Custom attribute values",
                "properties": {
                  "is_multi": {
                    "description": "If true, value must be an array",
                    "example": false,
                    "type": "boolean"
                  },
                  "type": {
                    "description": "Custom attributes type",
                    "enum": [
                      "URL",
                      "TEXT",
                      "NUMBER"
                    ],
                    "example": "NUMBER",
                    "type": "string",
                    "x-enum-varnames": [
                      "URL",
                      "TEXT",
                      "NUMBER"
                    ]
                  },
                  "value": {
                    "description": "Union of supported value for a custom attribute",
                    "oneOf": [
                      {
                        "description": "Value of TEXT/URL custom attribute",
                        "type": "string"
                      },
                      {
                        "description": "Value of multi TEXT/URL custom attribute",
                        "items": {
                          "description": "TEXT/URL Value",
                          "type": "string"
                        },
                        "type": "array"
                      },
                      {
                        "description": "Value of NUMBER custom attribute",
                        "format": "double",
                        "type": "number"
                      },
                      {
                        "description": "Values of multi NUMBER custom attribute",
                        "items": {
                          "description": "NUMBER value",
                          "format": "double",
                          "type": "number"
                        },
                        "type": "array"
                      }
                    ]
                  }
                },
                "required": [
                  "type",
                  "is_multi",
                  "value"
                ],
                "type": "object"
              },
              "type": {
                "default": "case",
                "description": "Case resource type",
                "enum": [
                  "case"
                ],
                "example": "case",
                "type": "string",
                "x-enum-varnames": [
                  "CASE"
                ]
              }
            },
            "required": [
              "attributes",
              "type"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "description": "Update case custom attribute payload",
  "required": true
}
