http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "The JSON:API request for updating a Confluent resource.",
        "properties": {
          "data": {
            "description": "JSON:API request for updating a Confluent resource.",
            "properties": {
              "attributes": {
                "description": "Attributes object for updating a Confluent resource.",
                "properties": {
                  "enable_custom_metrics": {
                    "default": false,
                    "description": "Enable the `custom.consumer_lag_offset` metric, which contains extra metric tags.",
                    "example": false,
                    "type": "boolean"
                  },
                  "resource_type": {
                    "description": "The resource type of the Resource. Can be `kafka`, `connector`, `ksql`, or `schema_registry`.",
                    "example": "kafka",
                    "type": "string"
                  },
                  "tags": {
                    "description": "A list of strings representing tags. Can be a single key, or key-value pairs separated by a colon.",
                    "example": [
                      "myTag",
                      "myTag2:myValue"
                    ],
                    "items": {
                      "type": "string"
                    },
                    "type": "array"
                  }
                },
                "required": [
                  "resource_type"
                ],
                "type": "object"
              },
              "id": {
                "description": "The ID associated with a Confluent resource.",
                "example": "resource-id-123",
                "type": "string"
              },
              "type": {
                "default": "confluent-cloud-resources",
                "description": "The JSON:API type for this request.",
                "enum": [
                  "confluent-cloud-resources"
                ],
                "example": "confluent-cloud-resources",
                "type": "string",
                "x-enum-varnames": [
                  "CONFLUENT_CLOUD_RESOURCES"
                ]
              }
            },
            "required": [
              "attributes",
              "type",
              "id"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "description": "Confluent payload",
  "required": true
}
