http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request to update a datastore's configuration such as its name or description.",
        "properties": {
          "data": {
            "description": "Data wrapper containing the datastore identifier and the attributes to update.",
            "properties": {
              "attributes": {
                "description": "Attributes that can be updated on a datastore.",
                "properties": {
                  "description": {
                    "description": "A human-readable description about the datastore.",
                    "type": "string"
                  },
                  "name": {
                    "description": "The display name of the datastore.",
                    "type": "string"
                  }
                },
                "type": "object"
              },
              "id": {
                "description": "The unique identifier of the datastore to update.",
                "type": "string"
              },
              "type": {
                "default": "datastores",
                "description": "The resource type for datastores.",
                "enum": [
                  "datastores"
                ],
                "example": "datastores",
                "type": "string",
                "x-enum-varnames": [
                  "DATASTORES"
                ]
              }
            },
            "required": [
              "type"
            ],
            "type": "object"
          }
        },
        "type": "object"
      }
    }
  },
  "required": true
}
