http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request for updating a monitor notification rule.",
        "properties": {
          "data": {
            "description": "Object to update a monitor notification rule.",
            "properties": {
              "attributes": {
                "additionalProperties": false,
                "description": "Attributes of the monitor notification rule.",
                "properties": {
                  "conditional_recipients": {
                    "description": "Use conditional recipients to define different recipients for different situations.",
                    "properties": {
                      "conditions": {
                        "description": "Conditions of the notification rule.",
                        "items": {
                          "description": "Conditions for `conditional_recipients`.",
                          "properties": {
                            "recipients": {
                              "description": "A list of recipients to notify. Uses the same format as the monitor `message` field. Must not start with an '@'.",
                              "example": [
                                "slack-test-channel",
                                "jira-test"
                              ],
                              "items": {
                                "description": "individual recipient.",
                                "maxLength": 255,
                                "type": "string"
                              },
                              "maxItems": 20,
                              "minItems": 1,
                              "type": "array",
                              "uniqueItems": true
                            },
                            "scope": {
                              "description": "The scope to which the monitor applied.",
                              "example": "transition_type:alert",
                              "maxLength": 3000,
                              "minLength": 1,
                              "type": "string"
                            }
                          },
                          "required": [
                            "scope",
                            "recipients"
                          ],
                          "type": "object"
                        },
                        "maxItems": 10,
                        "minItems": 1,
                        "type": "array"
                      },
                      "fallback_recipients": {
                        "description": "A list of recipients to notify. Uses the same format as the monitor `message` field. Must not start with an '@'.",
                        "example": [
                          "slack-test-channel",
                          "jira-test"
                        ],
                        "items": {
                          "description": "individual recipient.",
                          "maxLength": 255,
                          "type": "string"
                        },
                        "maxItems": 20,
                        "minItems": 1,
                        "type": "array",
                        "uniqueItems": true
                      }
                    },
                    "required": [
                      "conditions"
                    ],
                    "type": "object"
                  },
                  "filter": {
                    "description": "Filter used to associate the notification rule with monitors.",
                    "oneOf": [
                      {
                        "additionalProperties": false,
                        "description": "Filter monitors by tags. Monitors must match all tags.",
                        "properties": {
                          "tags": {
                            "description": "A list of monitor tags.",
                            "example": [
                              "team:product",
                              "host:abc"
                            ],
                            "items": {
                              "maxLength": 255,
                              "type": "string"
                            },
                            "maxItems": 20,
                            "minItems": 1,
                            "type": "array",
                            "uniqueItems": true
                          }
                        },
                        "required": [
                          "tags"
                        ],
                        "type": "object"
                      }
                    ]
                  },
                  "name": {
                    "description": "The name of the monitor notification rule.",
                    "example": "A notification rule name",
                    "maxLength": 1000,
                    "minLength": 1,
                    "type": "string"
                  },
                  "recipients": {
                    "description": "A list of recipients to notify. Uses the same format as the monitor `message` field. Must not start with an '@'.",
                    "example": [
                      "slack-test-channel",
                      "jira-test"
                    ],
                    "items": {
                      "description": "individual recipient.",
                      "maxLength": 255,
                      "type": "string"
                    },
                    "maxItems": 20,
                    "minItems": 1,
                    "type": "array",
                    "uniqueItems": true
                  }
                },
                "required": [
                  "name"
                ],
                "type": "object"
              },
              "id": {
                "description": "The ID of the monitor notification rule.",
                "example": "00000000-0000-1234-0000-000000000000",
                "type": "string"
              },
              "type": {
                "default": "monitor-notification-rule",
                "description": "Monitor notification rule resource type.",
                "enum": [
                  "monitor-notification-rule"
                ],
                "example": "monitor-notification-rule",
                "type": "string",
                "x-enum-varnames": [
                  "MONITOR_NOTIFICATION_RULE"
                ]
              }
            },
            "required": [
              "id",
              "attributes"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "description": "Request body to update the monitor notification rule.",
  "required": true
}
