http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request for creating a new monitor user template version.",
        "properties": {
          "data": {
            "description": "Monitor user template data.",
            "properties": {
              "attributes": {
                "additionalProperties": false,
                "description": "Attributes for a monitor user template.",
                "properties": {
                  "description": {
                    "description": "A brief description of the monitor user template.",
                    "example": "This is a template for monitoring user activity.",
                    "nullable": true,
                    "type": "string"
                  },
                  "monitor_definition": {
                    "additionalProperties": {},
                    "description": "A valid monitor definition in the same format as the [V1 Monitor API](https://docs.datadoghq.com/api/latest/monitors/#create-a-monitor).",
                    "example": {
                      "message": "You may need to add web hosts if this is consistently high.",
                      "name": "Bytes received on host0",
                      "query": "avg(last_5m):sum:system.net.bytes_rcvd{host:host0} > 100",
                      "type": "query alert"
                    },
                    "type": "object"
                  },
                  "tags": {
                    "description": "The definition of `MonitorUserTemplateTags` object.",
                    "example": [
                      "product:Our Custom App",
                      "integration:Azure"
                    ],
                    "items": {
                      "description": "Tags associated with the monitor user template. Must be key value. Only 'product' and 'integration' keys are\nallowed. The value is the name of the category to display the template under. Integrations can be filtered out in the UI.\n(Review note: This modeling of 'categories' is subject to change.)",
                      "example": "us-east1",
                      "minLength": 1,
                      "type": "string",
                      "uniqueItems": true
                    },
                    "type": "array"
                  },
                  "template_variables": {
                    "description": "The definition of `MonitorUserTemplateTemplateVariables` object.",
                    "items": {
                      "additionalProperties": false,
                      "description": "List of objects representing template variables on the monitor which can have selectable values.",
                      "properties": {
                        "available_values": {
                          "description": "Available values for the variable.",
                          "example": [
                            "value1",
                            "value2"
                          ],
                          "items": {
                            "minLength": 1,
                            "type": "string",
                            "uniqueItems": true
                          },
                          "type": "array"
                        },
                        "defaults": {
                          "description": "Default values of the template variable.",
                          "example": [
                            "defaultValue"
                          ],
                          "items": {
                            "minLength": 0,
                            "type": "string",
                            "uniqueItems": true
                          },
                          "type": "array"
                        },
                        "name": {
                          "description": "The name of the template variable.",
                          "example": "regionName",
                          "type": "string"
                        },
                        "tag_key": {
                          "description": "The tag key associated with the variable. This works the same as dashboard template variables.",
                          "example": "datacenter",
                          "type": "string"
                        }
                      },
                      "required": [
                        "name"
                      ],
                      "type": "object"
                    },
                    "type": "array"
                  },
                  "title": {
                    "description": "The title of the monitor user template.",
                    "example": "Postgres CPU Monitor",
                    "type": "string"
                  }
                },
                "required": [
                  "title",
                  "monitor_definition",
                  "tags"
                ],
                "type": "object"
              },
              "id": {
                "description": "The unique identifier.",
                "example": "00000000-0000-1234-0000-000000000000",
                "type": "string"
              },
              "type": {
                "default": "monitor-user-template",
                "description": "Monitor user template resource type.",
                "enum": [
                  "monitor-user-template"
                ],
                "example": "monitor-user-template",
                "type": "string",
                "x-enum-varnames": [
                  "MONITOR_USER_TEMPLATE"
                ]
              }
            },
            "required": [
              "id",
              "type",
              "attributes"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
