http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "A top-level wrapper for a schedule update request, referring to the `data` object with the new details.",
        "example": {
          "data": {
            "attributes": {
              "layers": [
                {
                  "effective_date": "2025-02-03T05:00:00Z",
                  "end_date": "2025-12-31T00:00:00Z",
                  "interval": {
                    "seconds": 3600
                  },
                  "members": [
                    {
                      "user": {
                        "id": "00000000-aba1-0000-0000-000000000000"
                      }
                    }
                  ],
                  "name": "Layer 1",
                  "restrictions": [
                    {
                      "end_day": "friday",
                      "end_time": "17:00:00",
                      "start_day": "monday",
                      "start_time": "09:00:00"
                    }
                  ],
                  "rotation_start": "2025-02-01T00:00:00Z"
                }
              ],
              "name": "On-Call Schedule Updated",
              "time_zone": "America/New_York"
            },
            "id": "3653d3c6-0c75-11ea-ad28-fb5701eabc7d",
            "relationships": {
              "teams": {
                "data": [
                  {
                    "id": "00000000-da3a-0000-0000-000000000000",
                    "type": "teams"
                  }
                ]
              }
            },
            "type": "schedules"
          }
        },
        "properties": {
          "data": {
            "description": "Contains all data needed to update an existing schedule, including its attributes (such as name and time zone) and any relationships to teams.",
            "properties": {
              "attributes": {
                "description": "Defines the updatable attributes for a schedule, such as name, time zone, and layers.",
                "properties": {
                  "layers": {
                    "description": "The updated list of layers (rotations) for this schedule.",
                    "items": {
                      "description": "Represents a layer within a schedule update, including rotation details, members,\nand optional restrictions.",
                      "properties": {
                        "effective_date": {
                          "description": "When this updated layer takes effect (ISO 8601 format).",
                          "example": "2025-02-03T05:00:00Z",
                          "format": "date-time",
                          "type": "string"
                        },
                        "end_date": {
                          "description": "When this updated layer should stop being active (ISO 8601 format).",
                          "example": "2025-12-31T00:00:00Z",
                          "format": "date-time",
                          "type": "string"
                        },
                        "id": {
                          "description": "A unique identifier for the layer being updated.",
                          "example": "00000000-0000-0000-0000-000000000001",
                          "type": "string"
                        },
                        "interval": {
                          "description": "Defines how often the rotation repeats, using a combination of days and optional seconds. Should be at least 1 hour.",
                          "properties": {
                            "days": {
                              "description": "The number of days in each rotation cycle.",
                              "example": 1,
                              "format": "int32",
                              "maximum": 400,
                              "type": "integer"
                            },
                            "seconds": {
                              "description": "Any additional seconds for the rotation cycle (up to 30 days).",
                              "example": 300,
                              "format": "int64",
                              "maximum": 2592000,
                              "type": "integer"
                            }
                          },
                          "type": "object"
                        },
                        "members": {
                          "description": "The members assigned to this layer.",
                          "items": {
                            "description": "Defines a single member within a schedule layer, including the reference to the underlying user.",
                            "properties": {
                              "user": {
                                "description": "Identifies the user participating in this layer as a single object with an `id`.",
                                "properties": {
                                  "id": {
                                    "description": "The user's ID.",
                                    "example": "00000000-aba1-0000-0000-000000000000",
                                    "type": "string"
                                  }
                                },
                                "type": "object"
                              }
                            },
                            "type": "object"
                          },
                          "type": "array"
                        },
                        "name": {
                          "description": "The name for this layer (for example, \"Secondary Coverage\").",
                          "example": "Primary On-Call Layer",
                          "type": "string"
                        },
                        "restrictions": {
                          "description": "Any time restrictions that define when this layer is active.",
                          "items": {
                            "description": "Defines a single time restriction rule with start and end times and the applicable weekdays.",
                            "properties": {
                              "end_day": {
                                "description": "A day of the week.",
                                "enum": [
                                  "monday",
                                  "tuesday",
                                  "wednesday",
                                  "thursday",
                                  "friday",
                                  "saturday",
                                  "sunday"
                                ],
                                "type": "string",
                                "x-enum-varnames": [
                                  "MONDAY",
                                  "TUESDAY",
                                  "WEDNESDAY",
                                  "THURSDAY",
                                  "FRIDAY",
                                  "SATURDAY",
                                  "SUNDAY"
                                ]
                              },
                              "end_time": {
                                "description": "Specifies the ending time for this restriction.",
                                "type": "string"
                              },
                              "start_day": {
                                "description": "A day of the week.",
                                "enum": [
                                  "monday",
                                  "tuesday",
                                  "wednesday",
                                  "thursday",
                                  "friday",
                                  "saturday",
                                  "sunday"
                                ],
                                "type": "string",
                                "x-enum-varnames": [
                                  "MONDAY",
                                  "TUESDAY",
                                  "WEDNESDAY",
                                  "THURSDAY",
                                  "FRIDAY",
                                  "SATURDAY",
                                  "SUNDAY"
                                ]
                              },
                              "start_time": {
                                "description": "Specifies the starting time for this restriction.",
                                "type": "string"
                              }
                            },
                            "type": "object"
                          },
                          "type": "array"
                        },
                        "rotation_start": {
                          "description": "The date/time at which the rotation begins (ISO 8601 format).",
                          "example": "2025-02-01T00:00:00Z",
                          "format": "date-time",
                          "type": "string"
                        }
                      },
                      "required": [
                        "effective_date",
                        "interval",
                        "members",
                        "name",
                        "rotation_start"
                      ],
                      "type": "object"
                    },
                    "type": "array"
                  },
                  "name": {
                    "description": "A short name for the schedule.",
                    "example": "Primary On-Call",
                    "type": "string"
                  },
                  "time_zone": {
                    "description": "The time zone used when interpreting rotation times.",
                    "example": "America/New_York",
                    "type": "string"
                  }
                },
                "required": [
                  "name",
                  "time_zone",
                  "layers"
                ],
                "type": "object"
              },
              "id": {
                "description": "The ID of the schedule to be updated.",
                "example": "3653d3c6-0c75-11ea-ad28-fb5701eabc7d",
                "type": "string"
              },
              "relationships": {
                "description": "Houses relationships for the schedule update, typically referencing teams.",
                "properties": {
                  "teams": {
                    "description": "Associates teams with this schedule in a data structure.",
                    "properties": {
                      "data": {
                        "description": "An array of team references for this schedule.",
                        "items": {
                          "description": "Relates a team to this schedule, identified by `id` and `type` (must be `teams`).",
                          "properties": {
                            "id": {
                              "description": "The unique identifier of the team in this relationship.",
                              "example": "00000000-da3a-0000-0000-000000000000",
                              "type": "string"
                            },
                            "type": {
                              "default": "teams",
                              "description": "Teams resource type.",
                              "enum": [
                                "teams"
                              ],
                              "example": "teams",
                              "type": "string",
                              "x-enum-varnames": [
                                "TEAMS"
                              ]
                            }
                          },
                          "required": [
                            "type",
                            "id"
                          ],
                          "type": "object"
                        },
                        "type": "array"
                      }
                    },
                    "type": "object"
                  }
                },
                "type": "object"
              },
              "type": {
                "default": "schedules",
                "description": "Schedules resource type.",
                "enum": [
                  "schedules"
                ],
                "example": "schedules",
                "type": "string",
                "x-enum-varnames": [
                  "SCHEDULES"
                ]
              }
            },
            "required": [
              "type",
              "id",
              "attributes"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
