http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request to update an org connection.",
        "properties": {
          "data": {
            "description": "Org connection update data.",
            "properties": {
              "attributes": {
                "description": "Attributes for updating an org connection.",
                "properties": {
                  "connection_types": {
                    "description": "Updated list of connection types.",
                    "example": [
                      "logs",
                      "metrics"
                    ],
                    "items": {
                      "description": "Available connection types between organizations.",
                      "enum": [
                        "logs",
                        "metrics"
                      ],
                      "example": "logs",
                      "type": "string",
                      "x-enum-varnames": [
                        "LOGS",
                        "METRICS"
                      ]
                    },
                    "minItems": 1,
                    "type": "array"
                  }
                },
                "required": [
                  "connection_types"
                ],
                "type": "object"
              },
              "id": {
                "description": "The unique identifier of the org connection.",
                "example": "f9ec96b0-8c8a-4b0a-9b0a-1b2c3d4e5f6a",
                "format": "uuid",
                "type": "string"
              },
              "type": {
                "description": "Org connection type.",
                "enum": [
                  "org_connection"
                ],
                "example": "org_connection",
                "type": "string",
                "x-enum-varnames": [
                  "ORG_CONNECTION"
                ]
              }
            },
            "required": [
              "type",
              "id",
              "attributes"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
