http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request object to update a resource filter.",
        "properties": {
          "data": {
            "description": "The definition of `UpdateResourceFilterRequestData` object.",
            "properties": {
              "attributes": {
                "description": "Attributes of a resource filter.",
                "example": {
                  "aws": {
                    "123456789": [
                      "environment:production",
                      "team:devops"
                    ]
                  },
                  "azure": {
                    "sub-001": [
                      "app:frontend"
                    ]
                  },
                  "gcp": {
                    "project-abc": [
                      "region:us-central1"
                    ]
                  }
                },
                "properties": {
                  "cloud_provider": {
                    "additionalProperties": {
                      "additionalProperties": {
                        "items": {
                          "description": "Tag filter in format \"key:value\"",
                          "example": "environment:production",
                          "type": "string"
                        },
                        "type": "array"
                      },
                      "type": "object"
                    },
                    "description": "A map of cloud provider names (e.g., \"aws\", \"gcp\", \"azure\") to a map of account/resource IDs and their associated tag filters.",
                    "type": "object"
                  },
                  "uuid": {
                    "description": "The UUID of the resource filter.",
                    "type": "string"
                  }
                },
                "required": [
                  "cloud_provider"
                ],
                "type": "object"
              },
              "id": {
                "description": "The `UpdateResourceEvaluationFiltersRequestData` `id`.",
                "example": "csm_resource_filter",
                "type": "string"
              },
              "type": {
                "description": "Constant string to identify the request type.",
                "enum": [
                  "csm_resource_filter"
                ],
                "example": "csm_resource_filter",
                "type": "string",
                "x-enum-varnames": [
                  "CSM_RESOURCE_FILTER"
                ]
              }
            },
            "required": [
              "attributes",
              "type"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
