http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Update request for a restriction policy.",
        "properties": {
          "data": {
            "description": "Restriction policy object.",
            "properties": {
              "attributes": {
                "description": "Restriction policy attributes.",
                "example": {
                  "bindings": []
                },
                "properties": {
                  "bindings": {
                    "description": "An array of bindings.",
                    "items": {
                      "description": "Specifies which principals are associated with a relation.",
                      "properties": {
                        "principals": {
                          "description": "An array of principals. A principal is a subject or group of subjects.\nEach principal is formatted as `type:id`. Supported types: `role`, `team`, `user`, and `org`.\nThe org ID can be obtained through the api/v2/current_user API.\nThe user principal type accepts service account IDs.",
                          "example": [
                            "role:00000000-0000-1111-0000-000000000000"
                          ],
                          "items": {
                            "description": "Subject or group of subjects. Each principal is formatted as `type:id`.\nSupported types: `role`, `team`, `user`, and `org`.\nThe org ID can be obtained through the api/v2/current_user API.\nThe user principal type accepts service account IDs.",
                            "type": "string"
                          },
                          "type": "array"
                        },
                        "relation": {
                          "description": "The role/level of access.",
                          "example": "editor",
                          "type": "string"
                        }
                      },
                      "required": [
                        "relation",
                        "principals"
                      ],
                      "type": "object"
                    },
                    "type": "array"
                  }
                },
                "required": [
                  "bindings"
                ],
                "type": "object"
              },
              "id": {
                "description": "The identifier, always equivalent to the value specified in the `resource_id` path parameter.",
                "example": "dashboard:abc-def-ghi",
                "type": "string"
              },
              "type": {
                "default": "restriction_policy",
                "description": "Restriction policy type.",
                "enum": [
                  "restriction_policy"
                ],
                "example": "restriction_policy",
                "type": "string",
                "x-enum-varnames": [
                  "RESTRICTION_POLICY"
                ]
              }
            },
            "required": [
              "type",
              "id",
              "attributes"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "description": "Restriction policy payload",
  "required": true
}
