http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request object that includes the Custom Rule to update.",
        "properties": {
          "data": {
            "description": "Object for a single WAF Custom Rule.",
            "properties": {
              "attributes": {
                "description": "Update a WAF custom rule.",
                "properties": {
                  "action": {
                    "description": "The definition of `ApplicationSecurityWafCustomRuleAction` object.",
                    "properties": {
                      "action": {
                        "default": "block_request",
                        "description": "Override the default action to take when the WAF custom rule would block.",
                        "enum": [
                          "redirect_request",
                          "block_request"
                        ],
                        "example": "block_request",
                        "type": "string",
                        "x-enum-varnames": [
                          "REDIRECT_REQUEST",
                          "BLOCK_REQUEST"
                        ]
                      },
                      "parameters": {
                        "description": "The definition of `ApplicationSecurityWafCustomRuleActionParameters` object.",
                        "properties": {
                          "location": {
                            "description": "The location to redirect to when the WAF custom rule triggers.",
                            "example": "/blocking",
                            "type": "string"
                          },
                          "status_code": {
                            "default": 403,
                            "description": "The status code to return when the WAF custom rule triggers.",
                            "example": 403,
                            "format": "int64",
                            "type": "integer"
                          }
                        },
                        "type": "object"
                      }
                    },
                    "type": "object"
                  },
                  "blocking": {
                    "description": "Indicates whether the WAF custom rule will block the request.",
                    "example": false,
                    "type": "boolean"
                  },
                  "conditions": {
                    "description": "Conditions for which the WAF Custom Rule will triggers, all conditions needs to match in order for the WAF\nrule to trigger.",
                    "items": {
                      "description": "One condition of the WAF Custom Rule.",
                      "properties": {
                        "operator": {
                          "description": "Operator to use for the WAF Condition.",
                          "enum": [
                            "match_regex",
                            "!match_regex",
                            "phrase_match",
                            "!phrase_match",
                            "is_xss",
                            "is_sqli",
                            "exact_match",
                            "!exact_match",
                            "ip_match",
                            "!ip_match",
                            "capture_data"
                          ],
                          "example": "match_regex",
                          "type": "string",
                          "x-enum-varnames": [
                            "MATCH_REGEX",
                            "NOT_MATCH_REGEX",
                            "PHRASE_MATCH",
                            "NOT_PHRASE_MATCH",
                            "IS_XSS",
                            "IS_SQLI",
                            "EXACT_MATCH",
                            "NOT_EXACT_MATCH",
                            "IP_MATCH",
                            "NOT_IP_MATCH",
                            "CAPTURE_DATA"
                          ]
                        },
                        "parameters": {
                          "description": "The scope of the WAF custom rule.",
                          "properties": {
                            "data": {
                              "description": "Identifier of a list of data from the denylist. Can only be used as substitution from the list parameter.",
                              "example": "blocked_users",
                              "type": "string"
                            },
                            "inputs": {
                              "description": "List of inputs on which at least one should match with the given operator.",
                              "items": {
                                "description": "Input from the request on which the condition should apply.",
                                "properties": {
                                  "address": {
                                    "description": "Input from the request on which the condition should apply.",
                                    "enum": [
                                      "server.db.statement",
                                      "server.io.fs.file",
                                      "server.io.net.url",
                                      "server.sys.shell.cmd",
                                      "server.request.method",
                                      "server.request.uri.raw",
                                      "server.request.path_params",
                                      "server.request.query",
                                      "server.request.headers.no_cookies",
                                      "server.request.cookies",
                                      "server.request.trailers",
                                      "server.request.body",
                                      "server.response.status",
                                      "server.response.headers.no_cookies",
                                      "server.response.trailers",
                                      "grpc.server.request.metadata",
                                      "grpc.server.request.message",
                                      "grpc.server.method",
                                      "graphql.server.all_resolvers",
                                      "usr.id",
                                      "http.client_ip"
                                    ],
                                    "example": "server.db.statement",
                                    "type": "string",
                                    "x-enum-varnames": [
                                      "SERVER_DB_STATEMENT",
                                      "SERVER_IO_FS_FILE",
                                      "SERVER_IO_NET_URL",
                                      "SERVER_SYS_SHELL_CMD",
                                      "SERVER_REQUEST_METHOD",
                                      "SERVER_REQUEST_URI_RAW",
                                      "SERVER_REQUEST_PATH_PARAMS",
                                      "SERVER_REQUEST_QUERY",
                                      "SERVER_REQUEST_HEADERS_NO_COOKIES",
                                      "SERVER_REQUEST_COOKIES",
                                      "SERVER_REQUEST_TRAILERS",
                                      "SERVER_REQUEST_BODY",
                                      "SERVER_RESPONSE_STATUS",
                                      "SERVER_RESPONSE_HEADERS_NO_COOKIES",
                                      "SERVER_RESPONSE_TRAILERS",
                                      "GRPC_SERVER_REQUEST_METADATA",
                                      "GRPC_SERVER_REQUEST_MESSAGE",
                                      "GRPC_SERVER_METHOD",
                                      "GRAPHQL_SERVER_ALL_RESOLVERS",
                                      "USR_ID",
                                      "HTTP_CLIENT_IP"
                                    ]
                                  },
                                  "key_path": {
                                    "description": "Specific path for the input.",
                                    "items": {
                                      "type": "string"
                                    },
                                    "type": "array"
                                  }
                                },
                                "required": [
                                  "address"
                                ],
                                "type": "object"
                              },
                              "type": "array"
                            },
                            "list": {
                              "description": "List of value to use with the condition. Only used with the phrase_match, !phrase_match, exact_match and\n!exact_match operator.",
                              "items": {
                                "type": "string"
                              },
                              "type": "array"
                            },
                            "options": {
                              "description": "Options for the operator of this condition.",
                              "properties": {
                                "case_sensitive": {
                                  "default": false,
                                  "description": "Evaluate the value as case sensitive.",
                                  "type": "boolean"
                                },
                                "min_length": {
                                  "default": 0,
                                  "description": "Only evaluate this condition if the value has a minimum amount of characters.",
                                  "format": "int64",
                                  "type": "integer"
                                }
                              },
                              "type": "object"
                            },
                            "regex": {
                              "description": "Regex to use with the condition. Only used with match_regex and !match_regex operator.",
                              "example": "path.*",
                              "type": "string"
                            },
                            "value": {
                              "description": "Store the captured value in the specified tag name. Only used with the capture_data operator.",
                              "example": "custom_tag",
                              "type": "string"
                            }
                          },
                          "required": [
                            "inputs"
                          ],
                          "type": "object"
                        }
                      },
                      "required": [
                        "operator",
                        "parameters"
                      ],
                      "type": "object"
                    },
                    "type": "array"
                  },
                  "enabled": {
                    "description": "Indicates whether the WAF custom rule is enabled.",
                    "example": false,
                    "type": "boolean"
                  },
                  "name": {
                    "description": "The Name of the WAF custom rule.",
                    "example": "Block request from bad useragent",
                    "type": "string"
                  },
                  "path_glob": {
                    "description": "The path glob for the WAF custom rule.",
                    "example": "/api/search/*",
                    "type": "string"
                  },
                  "scope": {
                    "description": "The scope of the WAF custom rule.",
                    "items": {
                      "description": "The scope of the WAF custom rule.",
                      "properties": {
                        "env": {
                          "description": "The environment scope for the WAF custom rule.",
                          "example": "prod",
                          "type": "string"
                        },
                        "service": {
                          "description": "The service scope for the WAF custom rule.",
                          "example": "billing-service",
                          "type": "string"
                        }
                      },
                      "required": [
                        "service",
                        "env"
                      ],
                      "type": "object"
                    },
                    "type": "array"
                  },
                  "tags": {
                    "additionalProperties": {
                      "type": "string"
                    },
                    "description": "Tags associated with the WAF Custom Rule. The concatenation of category and type will form the security\nactivity field associated with the traces.",
                    "maxProperties": 32,
                    "properties": {
                      "category": {
                        "description": "The category of the WAF Rule, can be either `business_logic`, `attack_attempt` or `security_response`.",
                        "enum": [
                          "attack_attempt",
                          "business_logic",
                          "security_response"
                        ],
                        "example": "business_logic",
                        "type": "string",
                        "x-enum-varnames": [
                          "ATTACK_ATTEMPT",
                          "BUSINESS_LOGIC",
                          "SECURITY_RESPONSE"
                        ]
                      },
                      "type": {
                        "description": "The type of the WAF rule, associated with the category will form the security activity.",
                        "example": "users.login.success",
                        "type": "string"
                      }
                    },
                    "required": [
                      "category",
                      "type"
                    ],
                    "type": "object"
                  }
                },
                "required": [
                  "enabled",
                  "blocking",
                  "name",
                  "tags",
                  "conditions"
                ],
                "type": "object"
              },
              "type": {
                "default": "custom_rule",
                "description": "The type of the resource. The value should always be `custom_rule`.",
                "enum": [
                  "custom_rule"
                ],
                "example": "custom_rule",
                "type": "string",
                "x-enum-varnames": [
                  "CUSTOM_RULE"
                ]
              }
            },
            "required": [
              "attributes",
              "type"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "description": "New definition of the WAF Custom Rule.",
  "required": true
}
