http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "id": {
            "type": "integer",
            "description": "Unique identifier of the asset type",
            "format": "int64",
            "readOnly": true,
            "example": 14000234324
          },
          "name": {
            "type": "string",
            "description": "Name of the asset type",
            "example": "Hardware"
          },
          "description": {
            "type": "string",
            "description": "Short description of the asset type in HTML format",
            "example": "Computer or Laptop Hardware"
          },
          "description_text": {
            "type": "string",
            "description": "Short description of the asset type in plain text format",
            "readOnly": true,
            "example": "Computer or Laptop Hardware"
          },
          "parent_asset_type_id": {
            "type": "integer",
            "description": "Unique identifier of the parent asset type",
            "format": "int64",
            "example": 14000234324
          }
        }
      }
    }
  },
  "required": true
}
