http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "id": {
            "type": "integer",
            "description": "Unique ID of the location",
            "format": "int64",
            "readOnly": true,
            "example": 14000234324
          },
          "name": {
            "type": "string",
            "description": "Name of the location",
            "example": "Apple Campus"
          },
          "parent_location_id": {
            "type": "integer",
            "description": "Unique ID of the parent location",
            "format": "int64",
            "example": 14000234324
          },
          "primary_contact_id": {
            "type": "integer",
            "description": "Unique ID of the primary contact (Primary contact is a requester. The primary contact name, email and phone number will be referenced from the requester details)",
            "format": "int64",
            "example": 14000234324
          },
          "address_line1": {
            "type": "string",
            "description": "Address Line 1",
            "example": "1 Infinite Loop"
          },
          "address_line2": {
            "type": "string",
            "description": "Address Line 2",
            "example": "1 Infinite Loop"
          },
          "address_city": {
            "type": "string",
            "description": "City",
            "example": "Cupertino"
          },
          "address_state": {
            "type": "string",
            "description": "State",
            "example": "California"
          },
          "address_country": {
            "type": "string",
            "description": "Country",
            "example": "US"
          },
          "address_zipcode": {
            "type": "string",
            "description": "Zip Code of the Location",
            "example": "95014"
          }
        }
      }
    }
  },
  "required": true
}
