http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "allOf": [
          {
            "title": "PurchaseOrder",
            "type": "object",
            "properties": {
              "id": {
                "minimum": 1,
                "type": "integer",
                "description": "ID of the purchase order",
                "format": "int64",
                "readOnly": true,
                "example": 10
              },
              "vendor_id": {
                "minimum": 1,
                "type": "integer",
                "description": "ID of the vendor with whom the order is placed",
                "format": "int64",
                "example": 100001633
              },
              "name": {
                "type": "string",
                "description": "Title of the purchase order",
                "example": "Procure Adobe License"
              },
              "po_number": {
                "type": "string",
                "description": "Purchase order number",
                "example": "PO-10"
              },
              "vendor_details": {
                "type": "string",
                "description": "Details of the vendor with whom the order is placed",
                "example": "1 Infinite Loop Cupertino California United States 95014"
              },
              "expected_delivery_date": {
                "type": "string",
                "description": "Expected delivery date of the purchase order",
                "format": "date-time",
                "example": "2020-01-01T05:00:01Z"
              },
              "created_at": {
                "type": "string",
                "description": "Created date and time of the purchase order",
                "format": "date-time",
                "readOnly": true,
                "example": "2021-08-30T04:22:14Z"
              },
              "updated_at": {
                "type": "string",
                "description": "Updated date and time of the purchase order",
                "format": "date-time",
                "readOnly": true,
                "example": "2021-08-30T04:22:14Z"
              },
              "created_by": {
                "minimum": 1,
                "type": "integer",
                "description": "ID of the agent who created purchase order",
                "format": "int64",
                "readOnly": true,
                "example": 1001001256
              },
              "status": {
                "type": "number",
                "description": "Status of the purchase order",
                "example": 20.0
              },
              "shipping_address": {
                "type": "string",
                "description": "Address to which the order should be shipped",
                "example": "1 Infinite Loop Cupertino California"
              },
              "billing_same_as_shipping": {
                "type": "boolean",
                "description": "True if Billing address is same as Shipping address",
                "example": true
              },
              "billing_address": {
                "type": "string",
                "description": "Address to which the order should be billed",
                "example": "1 Infinite Loop Cupertino California"
              },
              "currency_code": {
                "type": "string",
                "description": "Currency unit used in the transaction",
                "example": "USD",
                "enum": [
                  "AUD",
                  "THB",
                  "CAD",
                  "CNY",
                  "DKK",
                  "AED",
                  "USD",
                  "EGP",
                  "EUR",
                  "HUF",
                  "CHF",
                  "HKD",
                  "IDR",
                  "ILS",
                  "CZK",
                  "ISK",
                  "TRL",
                  "MYR",
                  "MXN",
                  "NOK",
                  "OMR",
                  "PHP",
                  "PLN",
                  "GBP",
                  "BWP",
                  "QAR",
                  "ZAR",
                  "BRL",
                  "RUB",
                  "INR",
                  "SAR",
                  "SGD",
                  "SEK",
                  "TWD",
                  "VND",
                  "KRW",
                  "JPY",
                  "NZD",
                  "JOD"
                ]
              },
              "conversion_rate": {
                "type": "number",
                "description": "Conversion rate to convert selected currency unit to helpdesk currency",
                "format": "float",
                "example": 1.0
              },
              "department_id": {
                "minimum": 1,
                "type": "integer",
                "description": "Unique ID of the department",
                "format": "int64",
                "example": 10123
              },
              "discount_percentage": {
                "maximum": 100,
                "minimum": 0,
                "type": "number",
                "description": "Percentage of discount on the order",
                "format": "float",
                "example": 2.0
              },
              "tax_percentage": {
                "minimum": 0,
                "type": "number",
                "description": "Percentage of tax on the order",
                "format": "float",
                "example": 5.0
              },
              "shipping_cost": {
                "minimum": 0,
                "type": "number",
                "description": "Total cost of shipping the order",
                "format": "float",
                "example": 10.0
              },
              "custom_fields": {
                "type": "object",
                "properties": {},
                "example": {
                  "manager": "andrea@freshservice.com",
                  "department": "IT"
                }
              }
            },
            "x-tags": [
              "Purchase Orders"
            ]
          },
          {
            "type": "object",
            "properties": {
              "purcahse_items": {
                "type": "array",
                "example": [
                  {
                    "item_type": 2,
                    "item_name": "Macbook Air",
                    "description": "Macbook Air",
                    "cost": 2,
                    "quantity": 1,
                    "tax_percentage": 5
                  }
                ],
                "items": {
                  "title": "PurchaseItem",
                  "type": "object",
                  "properties": {
                    "item_type": {
                      "type": "integer",
                      "description": "Type of item to be ordered",
                      "format": "int64",
                      "example": 2,
                      "enum": [
                        1,
                        2,
                        3
                      ]
                    },
                    "item_name": {
                      "type": "string",
                      "description": "Name of the items to be ordered",
                      "example": "Macbook Air"
                    },
                    "description": {
                      "type": "string",
                      "description": "Item description",
                      "example": "Macbook Air"
                    },
                    "cost": {
                      "minimum": 0,
                      "type": "number",
                      "description": "Cost of the item",
                      "example": 2.0
                    },
                    "quantity": {
                      "minimum": 1,
                      "type": "integer",
                      "description": "Quantity of item to be ordered",
                      "example": 1
                    },
                    "tax_percentage": {
                      "minimum": 0,
                      "type": "number",
                      "description": "Percentage of tax on item cost",
                      "example": 5.0
                    }
                  },
                  "x-tags": [
                    "Purchase Orders"
                  ]
                }
              }
            }
          }
        ]
      }
    }
  },
  "required": true
}
