http_endpoint.request_body_spec:
{
  "description": "change details that needs to be updated",
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "id": {
            "type": "integer",
            "description": "Unique identifier of the change",
            "format": "int64",
            "readOnly": true,
            "example": 140002342342
          },
          "requester_id": {
            "type": "integer",
            "description": "Unique identifier of the initiator of the change",
            "format": "int64",
            "example": 14000234234
          },
          "agent_id": {
            "type": "integer",
            "description": "Unique identifier of the agent to whom the change is assigned",
            "format": "int64",
            "example": 1400023498
          },
          "group_id": {
            "type": "integer",
            "description": "Unique identifier of the agent group to which the change is assigned",
            "format": "int64",
            "example": 1400097572
          },
          "priority": {
            "type": "integer",
            "description": "Priority of the change 1-Low, 2-Medium, 3-High, 4-Urgent",
            "format": "string",
            "example": 2
          },
          "impact": {
            "type": "integer",
            "description": "Impact of the change 1-Low, 2-Medium, 3-High",
            "format": "string",
            "example": 1
          },
          "status": {
            "type": "integer",
            "description": "Status identifier of the change. Open -> 1, Planning -> 2, Approval -> 3, Pending Release -> 4, Pending Review -> 5, closed -> 6",
            "format": "int32",
            "example": 1
          },
          "risk": {
            "type": "integer",
            "description": "Risk of the change 1-Low, 2-Medium, 3-High, 4-Very High",
            "format": "string",
            "example": 1
          },
          "change_type": {
            "type": "integer",
            "description": "Type of the change 1-minor, 2-standard, 3-major, 4-emergency",
            "format": "string",
            "example": 1
          },
          "approval_status": {
            "type": "integer",
            "description": "Approval status of the change 1-Requested, 2-Approved, 3-Rejected 4-Not_Requested",
            "format": "string",
            "readOnly": true,
            "example": 1
          },
          "subject": {
            "type": "string",
            "description": "Subject of the change",
            "example": "Replace Macbook"
          },
          "description": {
            "type": "string",
            "description": "Short description of the change in HTML format",
            "example": "<p>Replace damaged Macbook</p>"
          },
          "description_text": {
            "type": "string",
            "description": "Short description of the change in plain text format",
            "readOnly": true,
            "example": "Replace damaged Macbook"
          },
          "planned_start_date": {
            "type": "string",
            "description": "Timestamp at which change is starting",
            "format": "date-time",
            "example": "2021-11-24T11:30:00Z"
          },
          "planned_end_date": {
            "type": "string",
            "description": "Timestamp at which change is ending",
            "format": "date-time",
            "example": "2021-11-24T11:30:00Z"
          },
          "project_id": {
            "type": "integer",
            "description": "Unique ID of the associated project",
            "format": "int64",
            "example": 1400023894
          },
          "department_id": {
            "type": "integer",
            "description": "Unique ID of the department initiating the change",
            "format": "int64",
            "example": 14000495482
          },
          "category": {
            "type": "string",
            "description": "Category of the change",
            "example": "Hardware"
          },
          "sub_category": {
            "type": "string",
            "description": "Sub-category of the change",
            "example": "Computer"
          },
          "item_category": {
            "type": "string",
            "description": "Item of the change",
            "example": "Mac"
          },
          "created_at": {
            "type": "string",
            "description": "Timestamp at which change was created",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-11-22T16:58:45Z"
          },
          "updated_at": {
            "type": "string",
            "description": "Timestamp at which change was last updated",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-11-22T16:58:45Z"
          },
          "associated_release": {
            "type": "integer",
            "description": "Unique ID of the associated release",
            "format": "int64",
            "readOnly": true,
            "example": 140004343
          },
          "assets": {
            "type": "array",
            "description": "Assets associated with the Ticket",
            "example": [
              {
                "id": 14000234324,
                "display_id": 1453,
                "name": "Hardware-Monitor"
              }
            ],
            "items": {
              "type": "object",
              "properties": {
                "id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "id of the asset",
                  "format": "int64",
                  "readOnly": true,
                  "example": 14000234324
                },
                "display_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "display id of the asset that is used for all operations",
                  "format": "int64",
                  "readOnly": true,
                  "example": 1453
                },
                "name": {
                  "type": "string",
                  "description": "Display Name of the Asset",
                  "example": "Hardware-Monitor"
                },
                "description": {
                  "type": "string",
                  "description": "Description of the asset",
                  "example": "28-inch Hardware-Monitor"
                },
                "asset_type_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Id of the asset type.",
                  "format": "int64",
                  "example": 14000284324
                },
                "impact": {
                  "type": "string",
                  "description": "Impact of the asset (accepted values 'high' 'medium' 'low')",
                  "example": "low",
                  "enum": [
                    "low",
                    "medium",
                    "high"
                  ]
                },
                "author_type": {
                  "type": "string",
                  "description": "Asset created by source",
                  "readOnly": true,
                  "example": "User"
                },
                "usage_type": {
                  "type": "string",
                  "description": "Usage type of the asset  (accepted values are 'permanent' & 'loaner')",
                  "example": "loaner",
                  "enum": [
                    "permanent",
                    "loaner"
                  ]
                },
                "asset_tag": {
                  "type": "string",
                  "description": "Asset tag of the asset",
                  "example": "wxyzabcdefghij"
                },
                "user_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Used by of the asset",
                  "format": "int64",
                  "example": 14000234324
                },
                "department_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Department of the asset",
                  "format": "int64",
                  "example": 14000232343
                },
                "location_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Location of the asset",
                  "format": "int64",
                  "example": 140006394857
                },
                "agent_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Managed by of the asset",
                  "format": "int64",
                  "example": 14000234324
                },
                "group_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Managed by group of the asset",
                  "format": "int64",
                  "example": 14000234939
                },
                "assigned_on": {
                  "type": "string",
                  "description": "Date and time when the asset was assigned",
                  "format": "date-time",
                  "example": "2021-11-24T11:30:00Z"
                },
                "created_at": {
                  "type": "string",
                  "description": "Timestamp at which the asset was created",
                  "format": "date-time",
                  "readOnly": true,
                  "example": "2021-11-24T11:30:00Z"
                },
                "updated_at": {
                  "type": "string",
                  "description": "Timestamp at which the asset was last modified",
                  "format": "date-time",
                  "readOnly": true,
                  "example": "2021-11-24T11:30:00Z"
                },
                "type_fields": {
                  "type": "object",
                  "additionalProperties": true,
                  "description": "Fields specific to every asset type (Every asset will have default base fields and type specific fields)",
                  "example": {
                    "field1": "Value 1",
                    "field2": "Value 2"
                  }
                }
              }
            }
          },
          "associated_problems": {
            "type": "array",
            "description": "Unique IDs of the problems associated with the change request",
            "readOnly": true,
            "example": [
              140002394874,
              140003948543
            ],
            "items": {
              "type": "integer",
              "format": "int64"
            }
          },
          "incidents_caused_by_change": {
            "type": "array",
            "description": "Unique IDs of the incidents caused by this change request",
            "readOnly": true,
            "example": [
              1400034598,
              1400039485
            ],
            "items": {
              "type": "integer",
              "format": "int64"
            }
          },
          "tickets_initiating_change": {
            "type": "array",
            "description": "Unique IDs of the tickets initiating this change request",
            "readOnly": true,
            "example": [
              1400023984,
              1400034984
            ],
            "items": {
              "type": "integer",
              "format": "int64"
            }
          },
          "associated_project": {
            "type": "integer",
            "description": "Id of the associated release",
            "format": "int64",
            "readOnly": true,
            "example": 140002394874
          },
          "custom_fields": {
            "type": "object",
            "additionalProperties": {
              "type": "string"
            },
            "description": "Key value pairs containing the names and values of custom fields",
            "example": {
              "change_choices": "First Choice",
              "state": "TX"
            }
          },
          "planning_fields": {
            "type": "object",
            "properties": {
              "reason_for_change": {
                "type": "object",
                "properties": {
                  "description": {
                    "type": "string",
                    "description": "Reason for change",
                    "example": "<p>Reason for change</p>"
                  },
                  "description_text": {
                    "type": "string",
                    "description": "Reason for change in plain text format",
                    "readOnly": true,
                    "example": "Reason for Change"
                  }
                },
                "example": {
                  "description": "<div>Reason for change</div>",
                  "description_text": "Reason for change"
                }
              },
              "impact": {
                "type": "object",
                "properties": {
                  "description": {
                    "type": "string",
                    "description": "Impact",
                    "example": "<p>Impact</p>"
                  },
                  "description_text": {
                    "type": "string",
                    "description": "Impact in plain text format",
                    "readOnly": true,
                    "example": "Impact"
                  }
                },
                "example": {
                  "description": "<div>Impact</div>",
                  "description_text": "Impact"
                }
              },
              "rollout_plan": {
                "type": "object",
                "properties": {
                  "description": {
                    "type": "string",
                    "description": "Rollout Plan",
                    "example": "<div>Rollout plan</div>"
                  },
                  "description_text": {
                    "type": "string",
                    "description": "Rollout plan in plain text format",
                    "readOnly": true,
                    "example": "Rollout Plan"
                  }
                },
                "example": {
                  "description": "<div>Rollout plan</div>",
                  "description_text": "Rollout Plan"
                }
              },
              "backout_plan": {
                "type": "object",
                "properties": {
                  "description": {
                    "type": "string",
                    "description": "Backout Plan",
                    "example": "<div>Backout plan</div>"
                  },
                  "description_text": {
                    "type": "string",
                    "description": "Backout plan in plain text format",
                    "readOnly": true,
                    "example": "Backout Plan"
                  }
                },
                "example": {
                  "description": "<div>Backout plan</div>",
                  "description_text": "Backout Plan"
                }
              }
            },
            "example": {
              "reason_for_change": {
                "description": "<div>Reason for change</div>",
                "description_text": "Reason for change"
              },
              "impact": {
                "description": "<div>Impact</div>",
                "description_text": "Impact"
              },
              "rollout_plan": {
                "description": "<div>Rollout plan</div>",
                "description_text": "Rollout Plan"
              },
              "backout_plan": {
                "description": "<div>Backout plan</div>",
                "description_text": "Backout Plan"
              }
            }
          },
          "maintenance_window": {
            "type": "object",
            "properties": {
              "id": {
                "type": "integer",
                "description": "Unique identifier of the Change Window",
                "format": "int64",
                "example": 140009348572
              },
              "name": {
                "type": "string",
                "description": "Name of the Change Window",
                "readOnly": true,
                "example": "Router firmware upgrade"
              },
              "description": {
                "type": "string",
                "description": "Description of the Change Window",
                "readOnly": true,
                "example": "Router firmware upgrade"
              },
              "window_start_date": {
                "type": "string",
                "description": "Timestamp at which Change Window is starting",
                "format": "date-time",
                "readOnly": true,
                "example": "2021-11-24T11:30:00Z"
              },
              "window_end_date": {
                "type": "string",
                "description": "Timestamp at which Change Window is ending",
                "format": "date-time",
                "readOnly": true,
                "example": "2021-11-25T11:30:00Z"
              }
            },
            "example": {
              "id": 140009348572,
              "name": "Router firmware upgrade",
              "description": "Router firmware upgrade",
              "window_start_date": "2021-11-24T11:30:00Z",
              "window_end_date": "2021-11-25T11:30:00Z"
            }
          },
          "black_out_window": {
            "type": "object",
            "properties": {
              "id": {
                "type": "integer",
                "description": "Unique identifier of the Change Window",
                "format": "int64",
                "example": 140009348572
              },
              "name": {
                "type": "string",
                "description": "Name of the Change Window",
                "readOnly": true,
                "example": "Router firmware upgrade"
              },
              "description": {
                "type": "string",
                "description": "Description of the Change Window",
                "readOnly": true,
                "example": "Router firmware upgrade"
              },
              "window_start_date": {
                "type": "string",
                "description": "Timestamp at which Change Window is starting",
                "format": "date-time",
                "readOnly": true,
                "example": "2021-11-24T11:30:00Z"
              },
              "window_end_date": {
                "type": "string",
                "description": "Timestamp at which Change Window is ending",
                "format": "date-time",
                "readOnly": true,
                "example": "2021-11-25T11:30:00Z"
              }
            },
            "example": {
              "id": 140009348572,
              "name": "Router firmware upgrade",
              "description": "Router firmware upgrade",
              "window_start_date": "2021-11-24T11:30:00Z",
              "window_end_date": "2021-11-25T11:30:00Z"
            }
          }
        }
      }
    }
  },
  "required": true
}
