http_endpoint.request_body_spec:
{
  "description": "Agent who needs to be updated",
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "email",
          "first_name",
          "role_ids"
        ],
        "type": "object",
        "properties": {
          "id": {
            "type": "integer",
            "description": "Unique identifier of the agent",
            "format": "int64",
            "readOnly": true,
            "example": 14000234324
          },
          "first_name": {
            "type": "string",
            "description": "First Name of the agent",
            "example": "Andrea"
          },
          "occasional": {
            "type": "boolean",
            "description": "True if the agent is an occasional agent, and false if full-time agent.",
            "example": true
          },
          "last_name": {
            "type": "string",
            "description": "Last Name of the agent",
            "example": "Smith"
          },
          "job_title": {
            "type": "string",
            "description": "Job Title of the agent",
            "example": "Product Manager"
          },
          "email": {
            "type": "string",
            "description": "Email address of the agent",
            "example": "andrea@freshservice.com"
          },
          "work_phone_number": {
            "type": "string",
            "description": "Work phone number of the agent",
            "example": "+1-567-3492"
          },
          "mobile_phone_number": {
            "type": "string",
            "description": "Mobile phone number of the agent",
            "example": "+1-567-3492"
          },
          "reporting_manager_id": {
            "type": "integer",
            "description": "Unique identifier of the agent's reporting manager",
            "format": "int64",
            "example": 14000234324
          },
          "time_zone": {
            "type": "string",
            "description": "Time zone of the agent",
            "example": "Eastern Time (US & Canada)"
          },
          "language": {
            "type": "string",
            "description": "Language used by the agent",
            "example": "en"
          },
          "location_id": {
            "type": "integer",
            "description": "Unique identifier of the location associated with the agent",
            "format": "int64",
            "example": 14000234324
          },
          "scoreboard_level_id": {
            "type": "integer",
            "description": "Unique ID of the level of the agent in the Arcade. Possible values: * 1 - Beginner * 2 - Intermediate * 3 - Professional * 4 - Expert * 5 - Master * 6 - Guru\n",
            "format": "int64",
            "example": 2,
            "enum": [
              1,
              2,
              3,
              4,
              5,
              6
            ]
          },
          "scope": {
            "type": "object",
            "properties": {
              "ticket": {
                "type": "string",
                "example": "Global Access",
                "enum": [
                  "Global Access",
                  "Group Access",
                  "Restricted Access"
                ]
              },
              "problem": {
                "type": "string",
                "example": "Group Access",
                "enum": [
                  "Global Access",
                  "Group Access",
                  "Restricted Access"
                ]
              },
              "change": {
                "type": "string",
                "example": "Restricted Access",
                "enum": [
                  "Global Access",
                  "Group Access",
                  "Restricted Access"
                ]
              },
              "release": {
                "type": "string",
                "example": "Group Access",
                "enum": [
                  "Global Access",
                  "Group Access",
                  "Restricted Access"
                ]
              },
              "asset": {
                "type": "string",
                "example": "Global Access",
                "enum": [
                  "Global Access",
                  "Group Access"
                ]
              }
            },
            "description": "Access level of the agent in various modules",
            "readOnly": true,
            "example": {
              "ticket": "Global Access",
              "problem": "Global Access",
              "change": "Global Access",
              "release": "Global Access",
              "asset": "Global Access"
            }
          },
          "group_ids": {
            "type": "array",
            "description": "<DEPRECATED> Unique IDs of the agent groups associated with the agent",
            "example": [
              1400023424,
              1400023423
            ],
            "items": {
              "type": "integer",
              "format": "int64"
            }
          },
          "member_of": {
            "type": "array",
            "description": "Unique IDs of the agent groups that the agent is a member of",
            "example": [
              140002533,
              140009834
            ],
            "items": {
              "type": "integer",
              "format": "int64"
            }
          },
          "observer_of": {
            "type": "array",
            "description": "Unique IDs of the agent groups that the agent is an observer of",
            "example": [
              1300045345,
              1300049484
            ],
            "items": {
              "type": "integer",
              "format": "int64"
            }
          },
          "role_ids": {
            "type": "array",
            "description": "(DEPRECATED) Unique IDs of the agent roles associated with the agent",
            "example": [
              1300034059,
              1300094583
            ],
            "items": {
              "type": "integer",
              "format": "int64"
            }
          },
          "roles": {
            "type": "array",
            "example": [
              {
                "role_id": 14000234324
              }
            ],
            "items": {
              "type": "object",
              "properties": {
                "role_id": {
                  "type": "integer",
                  "description": "Unique identifier of the role assigned",
                  "format": "int64",
                  "example": 14000234324
                },
                "assignment_scope": {
                  "type": "string",
                  "description": "The scope in which the agent can use the permissions granted by this role. Possible values are entire_helpdesk (all plans), member_groups (Blossom, Garden, and Estate), specified_groups (Forest only), and assigned_items (all plans)",
                  "example": "entire_helpdesk",
                  "enum": [
                    "entire_helpdesk",
                    "member_groups",
                    "specified_groups",
                    "assigned_items"
                  ]
                },
                "groups": {
                  "type": "array",
                  "description": "Groups in which the permissions granted by the role applies. Returns an array of group identifiers when the assignment_scope is specified_groups, and null otherwise.",
                  "example": [
                    1400034334,
                    3523453453
                  ],
                  "items": {
                    "type": "integer",
                    "format": "int64"
                  }
                }
              }
            }
          },
          "last_login_at": {
            "type": "string",
            "description": "Timestamp of the agent's last successful login",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-11-22T16:58:45Z"
          },
          "active": {
            "type": "boolean",
            "description": "true if the user is active, and false if the user account has been deactivated.",
            "example": true
          },
          "has_logged_in": {
            "type": "boolean",
            "description": "true if the user has logged in to Freshservice at least once, and false otherwise.",
            "example": true
          },
          "created_at": {
            "type": "string",
            "description": "Timestamp at which the agent was created",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-11-22T16:58:45Z"
          },
          "updated_at": {
            "type": "string",
            "description": "Timestamp at which the agent was last modified",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-11-22T16:58:45Z"
          }
        }
      }
    }
  },
  "required": true
}
